	--DueDT,CLTDPID,DPID,
	--'',ISIN,'HO',@PARTICIPANTCODE,DPTYPE,DpstryTxRefId,'C',DPTYPE,BDPID,BCLTDPID,
	--'',0,0,GETDATE(),0 FROM #DLIVERYDPO WHERE QtyORShrtQty > 0 AND TXIND IN ('POT1','POT2','POT3','EXC1','EXC2')
	
	
