	WHERE TXIND IN ( 'POT1', 'POT2', 'POT3') 

SELECT DISTINCT DT.*, FLAG = 'R'
INTO #DEL
FROM DELIVERYCLT DT, (SELECT TCKRSYMB, CLNTID, SCTYSRS, STTLMTP,SCTIESSTTLMTXID 
