    AND DT.PARTY_CODE = ALLOC.ClntId
    AND DT.SERIES = ALLOC.SCTYSRS
	AND DT.SETT_NO = @Sett_no
	AND DT.INOUT = 'O';

	DECLARE @@DELCLT CURSOR,
			@@QTYCUR CURSOR
	--**OBLIGATION BASED ALLOCATION TO UCC**----
		 SET @@DELCLT = CURSOR FOR         
		 SELECT
			DT.Scrip_CD,
			DT.Series,
			DT.PARTY_CODE,
			sum(DT.Qty) as Qty,FLAG
		FROM #DEL DT
		GROUP BY DT.Scrip_CD, DT.Series, DT.Party_Code,Flag
		ORDER BY DT.PARTY_CODE, SCRIP_CD, SERIES

		OPEN @@DELCLT                
		FETCH NEXT FROM @@DELCLT INTO @@SCRIP_CD,@@SERIES,@@PARTY_CODE,@@DELQTY,@@FLAG 
			WHILE @@FETCH_STATUS = 0                 
			BEGIN  
				SET @@QTYCUR = CURSOR FOR
				  SELECT ISNULL(SUM(QtyORShrtQty),0),ISNULL(DPSTRYTXREFID,'0') FROM #DLIVERYDPO                 
				  WHERE SctiesSttlmTxId = @SETT_NO                 
				  AND ClntId = @@PARTY_CODE                
				  AND TCKRSYMB  = @@SCRIP_CD                 
				  AND SctySrs = @@SERIES
				  AND TXIND IN ( 'POT1', 'POT2', 'POT3')
				  GROUP BY DPSTRYTXREFID
				               
				 Open @@QtyCur
				 Fetch next from @@QtyCur into @@Qty,@@FROMNO
					 While @@Fetch_status = 0 
					 begin
						-- select 'DPO qty' + CONVERT(varchar,@@Qty)
						-- select 'delclt qty' +  CONVERT(varchar,@@DELQTY)
						 if @@DelQty >= @@Qty
						 Begin
