							CASE WHEN BDPTYPE = 'CDSL' THEN @DPCLTNO_CDSL ELSE @DPCLTNO_NSDL END,GETDATE(),'',9 
							FROM #DLIVERYDPO WHERE @@QTY > 0 AND SctiesSttlmTxId = @SETT_NO AND ClntId = @@PARTY_CODE AND TCKRSYMB  = @@SCRIP_CD                 
							AND SctySrs = @@SERIES   
							AND DpstryTxRefId = @@FROMNO
	
							INSERT INTO DELTRANS_PROCESS 
							SELECT 0,SctiesSttlmTxId,SttlmTp,@REFNO,TCODE,904,ClntId,
							TckrSymb,SctySrs,QtyORShrtQty,DpstryTxRefId,DpstryTxRefId,ISIN,DpstryTxRefId,'PAY-OUT','DPO PAYOUT','D','0',QtyORShrtQty,
							DPTYPE,DPID,CLTDPID,'HO',@PARTICIPANTCODE,0,'','','','DEMAT',DueDT,0,1,'',Bdptype,
							BDPID = CASE WHEN BDPTYPE = 'CDSL' THEN @DPID_CDSL ELSE @DPID_NSDL END,
							CASE WHEN BDPTYPE = 'CDSL' THEN @DPCLTNO_CDSL ELSE @DPCLTNO_NSDL END,GETDATE(),'',9  
							FROM #DLIVERYDPO WHERE  @@QTY > 0 AND SctiesSttlmTxId = @SETT_NO AND ClntId = @@PARTY_CODE AND TCKRSYMB  = @@SCRIP_CD                 
							AND SctySrs = @@SERIES
							AND DpstryTxRefId = @@FROMNO
						 END
						ELSE IF @@DELQTY <> @@QTY AND @@QTY > 0
						 BEGIN
							INSERT INTO DEMATTRANS
							SELECT SctiesSttlmTxId,SttlmTp,@REFNO,
							DpstryTxRefId,906,CASE WHEN @REFNO = '110' THEN 'NSE' ELSE 'BSE' END,TckrSymb,SctySrs,@@QTY,
							DueDT,CLTDPID,DPID,
