  INSERT  INTO Dustbin.DBO.TBL_PENDING_OBL_BSEDB_DUST             
  SELECT *,TRANSDATE=@TRANSDATE FROM #DELAUCSHORT1                   
  --WHERE SELLSHORTAGE > 0               
  ORDER BY SETT_NO,SETT_TYPE,SCRIP_CD,SERIES,PARTY_CODE       
  DROP TABLE #DELAUCSHORT1       
