-- PROCEDURE dbo.PROC_PENDING_OBL_MSAJAG_DUST
-- --------------------------------------------------
    
    
    
CREATE PROC [dbo].[PROC_PENDING_OBL_MSAJAG_DUST]    
(    
 @TRANSDATE VARCHAR(11)    
)    
AS    
    
-- EXEC PROC_PENDING_OBL @TRANSDATE = 'FEB 18 2021'    
DECLARE @S_SETT_NO VARCHAR(7),    
  @S_SETT_TYPE VARCHAR(2),    
  @SETTCUR CURSOR    
       
DELETE FROM Dustbin.DBO.TBL_PENDING_OBL_MSAJAG_DUST    
--WHERE TRANSDATE = @TRANSDATE    
    
SELECT SETT_NO,SETT_TYPE,REFNO,TCODE,TRTYPE,PARTY_CODE,SCRIP_CD,SERIES,QTY,FROMNO,TONO,CERTNO,FOLIONO,              
HOLDERNAME,REASON,DRCR,DELIVERED,ORGQTY,DPTYPE,DPID,CLTDPID,BRANCHCD,PARTIPANTCODE,SLIPNO,BATCHNO,              
ISETT_NO,ISETT_TYPE,SHARETYPE,TRANSDATE,FILLER1,FILLER2,FILLER3,BDPTYPE,BDPID,BCLTDPID,FILLER4,FILLER5              
INTO #DELTRANS_DATA               
FROM [ANGELDEMAT].MSAJAG.dbo.DELTRANS              
WHERE 1 = 2               
     
SELECT SETT_NO,SETT_TYPE,SCRIP_CD,SERIES,PARTY_CODE,                
BUYQTY=QTY,                
SELLQTY=QTY                
INTO #DELCLT               
FROM [ANGELDEMAT].MSAJAG.dbo.DELIVERYCLT              
WHERE 1 = 2           
              
SELECT * INTO #SETT_MST FROM [ANGELDEMAT].MSAJAG.dbo.SETT_MST      
WHERE SEC_PAYIN > @TRANSDATE AND START_DATE <= @TRANSDATE + ' 23:59'    
AND SETT_TYPE IN ('N','W','L','P','F')    
    
SET @SETTCUR = CURSOR FOR              
SELECT SETT_NO,SETT_TYPE FROM #SETT_MST              
ORDER BY SETT_NO      
OPEN @SETTCUR              
FETCH NEXT FROM @SETTCUR INTO @S_SETT_NO, @S_SETT_TYPE              
WHILE @@FETCH_STATUS = 0               
BEGIN              
 INSERT INTO #DELTRANS_DATA               
 SELECT SETT_NO,SETT_TYPE,REFNO,TCODE,TRTYPE,PARTY_CODE,SCRIP_CD,SERIES,QTY,FROMNO,TONO,CERTNO,FOLIONO,              
 HOLDERNAME,REASON,DRCR,DELIVERED,ORGQTY,DPTYPE,DPID,CLTDPID,BRANCHCD,PARTIPANTCODE,SLIPNO,BATCHNO,              
 ISETT_NO,ISETT_TYPE,SHARETYPE,TRANSDATE,FILLER1,FILLER2,FILLER3,BDPTYPE,BDPID,BCLTDPID,FILLER4,FILLER5               
 FROM [ANGELDEMAT].MSAJAG.dbo.DELTRANS WITH(NOLOCK)              
 WHERE SETT_NO = @S_SETT_NO              
 AND SETT_TYPE = @S_SETT_TYPE              
 AND FILLER2 = 1               
 AND TRTYPE <> 906               
 AND PARTY_CODE <> 'BROKER'             
 AND TRANSDATE <= @TRANSDATE    
     
 /*    
 INSERT INTO #DELTRANS_DATA               
 SELECT SETT_NO,SETT_TYPE,REFNO,TCODE,TRTYPE,PARTY_CODE,SCRIP_CD,SERIES,QTY,FROMNO,TONO,CERTNO,FOLIONO,              
 HOLDERNAME,REASON,DRCR,DELIVERED,ORGQTY,DPTYPE,DPID,CLTDPID,BRANCHCD,PARTIPANTCODE,SLIPNO,BATCHNO,              
 ISETT_NO,ISETT_TYPE,SHARETYPE,TRANSDATE,FILLER1,FILLER2,FILLER3,BDPTYPE,BDPID,BCLTDPID,FILLER4,FILLER5               
 FROM DELTRANS_REPORT WITH(INDEX(DELHOLD), NOLOCK)              
 WHERE SETT_NO = @S_SETT_NO              
 AND SETT_TYPE = @S_SETT_TYPE              
 AND FILLER2 = 1               
 AND TRTYPE <> 906               
 AND PARTY_CODE <> 'BROKER'             
 AND TRANSDATE <= @TRANSDATE    
 */    
      
INSERT INTO #DELCLT    
SELECT SETT_NO,SETT_TYPE,SCRIP_CD,SERIES,PARTY_CODE,                
BUYQTY=SUM(CASE WHEN INOUT = 'O' THEN QTY ELSE 0 END),                
SELLQTY=SUM(CASE WHEN INOUT = 'I' THEN QTY ELSE 0 END)    
FROM [ANGELDEMAT].MSAJAG.dbo.DELIVERYCLT              
WHERE SETT_NO = @S_SETT_NO              
AND SETT_TYPE = @S_SETT_TYPE       
GROUP BY SETT_NO,SETT_TYPE,SCRIP_CD,SERIES,PARTY_CODE          
    
UNION    
    
SELECT SETT_NO,SETT_TYPE,SCRIP_CD,SERIES,PARTY_CODE,                
BUYQTY=SUM(CASE WHEN INOUT = 'O' THEN QTY ELSE 0 END),                
SELLQTY=SUM(CASE WHEN INOUT = 'I' THEN QTY ELSE 0 END)    
FROM [ANGELDEMAT].MSAJAG.dbo.DELIVERYCLT_REPORT              
WHERE SETT_NO = @S_SETT_NO              
AND SETT_TYPE = @S_SETT_TYPE       
GROUP BY SETT_NO,SETT_TYPE,SCRIP_CD,SERIES,PARTY_CODE           
                            
SELECT D.SETT_NO,D.SETT_TYPE,D.PARTY_CODE, D.SCRIP_CD,D.SERIES,                
BUYTRADEQTY = BUYQTY, BUYRECQTY=SUM(CASE WHEN DRCR = 'D' THEN ISNULL(DE.QTY,0) ELSE 0 END) ,                
SELLTRADEQTY = SELLQTY , SELLRECQTY=SUM(CASE WHEN DRCR = 'C' THEN ISNULL(DE.QTY,0) ELSE 0 END),            
FLAG = 0 , ISIN=CONVERT(VARCHAR(12),'')                        
INTO #DELAUCNEW FROM              
#DELCLT D LEFT OUTER JOIN #DELTRANS_DATA DE                 
ON ( DE.SETT_NO = D.SETT_NO AND DE.SETT_TYPE = D.SETT_TYPE AND DE.SCRIP_CD = D.SCRIP_CD                
AND DE.SERIES = D.SERIES AND DE.PARTY_CODE = D.PARTY_CODE AND FILLER2 = 1 AND SHARETYPE <> (CASE WHEN D.SETT_TYPE NOT LIKE 'A%' THEN 'AUCTION' ELSE '' END) )                
WHERE  D.SETT_NO >= @S_SETT_NO AND D.SETT_NO <= @S_SETT_NO                  
AND D.SETT_TYPE = @S_SETT_TYPE                
GROUP BY D.SETT_NO,D.SETT_TYPE,D.PARTY_CODE,D.SCRIP_CD,D.SERIES,BUYQTY,SELLQTY            
HAVING ( BUYQTY <> SUM(CASE WHEN DRCR = 'D' THEN ISNULL(DE.QTY,0) ELSE 0 END)                 
    OR SELLQTY <> SUM(CASE WHEN DRCR = 'C' THEN ISNULL(DE.QTY,0) ELSE 0 END) )                
                
INSERT INTO #DELAUCNEW                
SELECT D.SETT_NO,D.SETT_TYPE,D.PARTY_CODE,D.SCRIP_CD,D.SERIES,                
BUYTRADEQTY = 0, BUYRECQTY=SUM(CASE WHEN DRCR = 'D' THEN ISNULL(D.QTY,0) ELSE 0 END) ,                
SELLTRADEQTY = 0, SELLRECQTY=SUM(CASE WHEN DRCR = 'C' THEN ISNULL(D.QTY,0) ELSE 0 END),            
FLAG = 0 , ISIN=CONVERT(VARCHAR(12),'')                        
FROM #DELTRANS_DATA D                 
WHERE  D.SETT_NO >= @S_SETT_NO AND D.SETT_NO <= @S_SETT_NO                  
AND D.SETT_TYPE = @S_SETT_TYPE                
AND FILLER2 = 1 AND SHARETYPE <> (CASE WHEN D.SETT_TYPE NOT LIKE 'A%' THEN 'AUCTION' ELSE '' END)              
AND D.PARTY_CODE NOT IN ( SELECT PARTY_CODE FROM #DELCLT DE                
     WHERE DE.SETT_NO = D.SETT_NO AND DE.SETT_TYPE = D.SETT_TYPE AND DE.SCRIP_CD = D.SCRIP_CD                
     AND DE.SERIES = D.SERIES AND DE.PARTY_CODE = D.PARTY_CODE  )                
GROUP BY D.SETT_NO,D.SETT_TYPE,D.PARTY_CODE,D.SCRIP_CD,D.SERIES            
HAVING SUM(CASE WHEN DRCR = 'D' THEN ISNULL(D.QTY,0) ELSE 0 END) <> SUM(CASE WHEN DRCR = 'C' THEN ISNULL(D.QTY,0) ELSE 0 END)                
    
IF @S_SETT_TYPE <> 'W'                 
BEGIN                 
 --select * from #DELTRANS_DATA where Party_code='KNPR4092'     
 --select * from #DELAUCNEW  where Party_code='KNPR4092'              
 SELECT SETT_NO,SETT_TYPE,PARTY_CODE,SCRIP_CD,SERIES,BUYTRADEQTY,BUYRECQTY,SELLTRADEQTY,SELLRECQTY,                
 BUYSHORTAGE=(CASE WHEN ( BUYTRADEQTY - SELLTRADEQTY - BUYRECQTY + SELLRECQTY ) > 0                 
            THEN ( BUYTRADEQTY - SELLTRADEQTY - BUYRECQTY + SELLRECQTY )                
     ELSE 0 END ) ,                
 SELLSHORTAGE=(CASE WHEN ( BUYTRADEQTY - SELLTRADEQTY - BUYRECQTY + SELLRECQTY ) < 0                 
            THEN ABS( BUYTRADEQTY - SELLTRADEQTY - BUYRECQTY + SELLRECQTY )                
     ELSE 0 END ), CL_RATE = CONVERT(NUMERIC(18,4),0),            
FLAG = 0 , ISIN=CONVERT(VARCHAR(12),'')                            
 INTO #DELAUCSHORT FROM #DELAUCNEW                
 WHERE ( BUYTRADEQTY - SELLTRADEQTY - BUYRECQTY + SELLRECQTY ) <> 0    
     
 --select * from #DELAUCSHORT where Party_code='KNPR4092'                 
      
  UPDATE #DELAUCSHORT SET ISIN=MULTIISIN.ISIN FROM [ANGELDEMAT].MSAJAG.dbo.MULTIISIN                      
  WHERE MULTIISIN.SCRIP_CD = #DELAUCSHORT.SCRIP_CD AND MULTIISIN.SERIES = #DELAUCSHORT.SERIES            
   AND VALID = 1             
  INSERT  INTO Dustbin.DBO.TBL_PENDING_OBL_MSAJAG_DUST      
 SELECT *,TRANSDATE=@TRANSDATE FROM #DELAUCSHORT                
 --WHERE SELLSHORTAGE > 0           
 ORDER BY SETT_NO,SETT_TYPE,SCRIP_CD,SERIES,PARTY_CODE                
 DROP TABLE #DELAUCSHORT    
 END    
ELSE    
BEGIN    
SELECT SETT_NO,SETT_TYPE,PARTY_CODE,SCRIP_CD,SERIES,BUYTRADEQTY,BUYRECQTY,SELLTRADEQTY,SELLRECQTY,                
 BUYSHORTAGE=(CASE WHEN ( BUYTRADEQTY > BUYRECQTY )                
            THEN BUYTRADEQTY - BUYRECQTY                 
   ELSE                 
      CASE WHEN ( BUYTRADEQTY < BUYRECQTY )                
      THEN (CASE WHEN ( SELLTRADEQTY < SELLRECQTY AND (BUYRECQTY - BUYTRADEQTY - SELLRECQTY + SELLTRADEQTY) < 0 )                     
        THEN ABS(BUYRECQTY - BUYTRADEQTY - SELLRECQTY + SELLTRADEQTY)                
             ELSE 0                 
          END )                  
           ELSE (CASE WHEN SELLTRADEQTY < SELLRECQTY                 
      THEN SELLRECQTY - SELLTRADEQTY                   
      ELSE 0 END ) END                
     END ),                
 SELLSHORTAGE=(CASE WHEN ( SELLTRADEQTY > SELLRECQTY )                
            THEN SELLTRADEQTY - SELLRECQTY                 
 ELSE              
      CASE WHEN ( SELLTRADEQTY < SELLRECQTY )                
      THEN (CASE WHEN ( BUYTRADEQTY < BUYRECQTY AND (SELLRECQTY - SELLTRADEQTY - BUYRECQTY + BUYTRADEQTY ) < 0 )                     
        THEN ABS(SELLRECQTY - SELLTRADEQTY - BUYRECQTY + BUYTRADEQTY )                
     ELSE 0                 
          END )                  
           ELSE (CASE WHEN BUYTRADEQTY < BUYRECQTY                 
      THEN BUYRECQTY - BUYTRADEQTY                   
      ELSE 0 END ) END                
     END ), CL_RATE = CONVERT(NUMERIC(18,4),0),            
 FLAG = 0 , ISIN=CONVERT(VARCHAR(12),'')                            
 INTO #DELAUCSHORT1 FROM #DELAUCNEW                
 WHERE ( (CASE WHEN ( SELLTRADEQTY > SELLRECQTY )                
            THEN SELLTRADEQTY - SELLRECQTY                 
     ELSE                 
      CASE WHEN ( SELLTRADEQTY < SELLRECQTY )                
      THEN (CASE WHEN ( BUYTRADEQTY < BUYRECQTY AND (SELLRECQTY - SELLTRADEQTY - BUYRECQTY + BUYTRADEQTY ) < 0 )                     
        THEN ABS(SELLRECQTY - SELLTRADEQTY - BUYRECQTY + BUYTRADEQTY )                
             ELSE 0                 
          END )                  
           ELSE (CASE WHEN BUYTRADEQTY < BUYRECQTY                 
      THEN BUYRECQTY - BUYTRADEQTY                   
      ELSE 0 END ) END                
     END )  <> 0                 
   OR                  
  (CASE WHEN ( BUYTRADEQTY > BUYRECQTY )                
            THEN BUYTRADEQTY - BUYRECQTY                 
     ELSE                 
      CASE WHEN ( BUYTRADEQTY < BUYRECQTY )                
      THEN (CASE WHEN ( SELLTRADEQTY < SELLRECQTY AND (BUYRECQTY - BUYTRADEQTY - SELLRECQTY + SELLTRADEQTY) < 0 )                     
        THEN ABS(BUYRECQTY - BUYTRADEQTY - SELLRECQTY + SELLTRADEQTY)                
             ELSE 0                 
          END )                  
           ELSE (CASE WHEN SELLTRADEQTY < SELLRECQTY                 
      THEN SELLRECQTY - SELLTRADEQTY                   
      ELSE 0 END ) END                
     END ) <> 0 )     
      
                        
  UPDATE #DELAUCSHORT1 SET ISIN=MULTIISIN.ISIN FROM [ANGELDEMAT].MSAJAG.dbo.MULTIISIN                      
  WHERE MULTIISIN.SCRIP_CD = #DELAUCSHORT1.SCRIP_CD AND MULTIISIN.SERIES = #DELAUCSHORT1.SERIES            
   AND VALID = 1             
         
  INSERT  INTO Dustbin.DBO.TBL_PENDING_OBL_MSAJAG_DUST           
  SELECT *,TRANSDATE=@TRANSDATE FROM #DELAUCSHORT1                 
  --WHERE SELLSHORTAGE > 0             
  ORDER BY SETT_NO,SETT_TYPE,SCRIP_CD,SERIES,PARTY_CODE     
  DROP TABLE #DELAUCSHORT1     
END                 
 DROP TABLE #DELAUCNEW                  
 FETCH NEXT FROM @SETTCUR INTO @S_SETT_NO, @S_SETT_TYPE               
END              
CLOSE @SETTCUR              
DEALLOCATE @SETTCUR              
    
DROP TABLE #DELCLT    
DROP TABLE #DELTRANS_DATA    
DROP TABLE #SETT_MST

GO

-- --------------------------------------------------
-- PROCEDURE dbo.PUNIT_EXECUTION_REPORT
-- --------------------------------------------------
CREATE  PROCEDURE [dbo].[PUNIT_EXECUTION_REPORT]      
( @TRANSDATE_FROM DATETIME,      
@TRANSDATE_TO DATETIME,      
 @BDPID VARCHAR(16),      
 @BCltDpId VARCHAR(16)  
)      
AS      
BEGIN      
             
CREATE TABLE [#Deltrans_report] (                  
         [SNo] [Numeric]  NOT NULL ,                  
 [Sett_No] [varchar] (7) NOT NULL ,          
 [Sett_Type] [varchar] (2) NOT NULL ,                  
 [Tcode] [numeric](18, 0) NOT NULL ,                  
 [Trtype] [numeric](18, 0) NOT NULL ,                  
 [Party_Code] [varchar] (10) NOT NULL ,                  
 [Short_Name] [varchar] (40) NOT NULL ,                  
 [Scrip_Cd] [varchar] (12) NOT NULL ,                  
 [Series] [varchar] (3) NOT NULL ,                  
 [Qty] [numeric](18, 0) NOT NULL ,                  
 [Fromno] [varchar] (16) NULL ,                  
 [Certno] [varchar] (16) NULL ,                  
 [Reason] [varchar] (100) NOT NULL ,                  
 [Drcr] [char] (1) NOT NULL ,                  
 [Delivered] [char] (1) NOT NULL ,                  
 [Dptype] [varchar] (10) NULL ,                  
 [Dpid] [varchar] (16) NULL ,                  
 [Cltdpid] [varchar] (16) NULL ,                  
 [Isett_No] [varchar] (7) NULL ,                  
 [Isett_Type] [varchar] (2) NULL ,                  
 [Sharetype] [varchar] (8) NULL ,                  
 [Transdate] [datetime] NOT NULL ,                  
 [Filler2] [int] NULL ,                  
 [Bdptype] [varchar] (10) NULL ,                  
 [Bdpid] [varchar] (16) NULL ,                  
 [Bcltdpid] [varchar] (16) NULL,                  
    [HolderName] [varchar] (100) NOT NULL                  
)                  
                 
                             
Insert Into #Deltrans_report                  
Select                  
    x.SNo,                  
    x.Sett_No,                  
    x.Sett_Type,                  
    x.Tcode,                  
    x.Trtype,                  
    x.Party_Code,                  
    c1.Short_Name,                  
    x.Scrip_Cd,                  
    x.Series,                  
    x.Qty,                  
    x.Fromno,                  
    x.Certno,                  
    x.Reason,                  
    x.Drcr,                  
    x.Delivered,                  
    x.Dptype,                  
    x.Dpid,                  
    x.Cltdpid,                  
    x.Isett_No,                  
    x.Isett_Type,                  
    x.Sharetype,                  
    x.Transdate,                  
    x.Filler2,                  
    x.Bdptype,                  
    x.Bdpid,                  
    x.Bcltdpid,                  
 x.HolderName                  
from                  
      MSAJAG..Deltrans_report x (Nolock) ,                  
      MSAJAG..Client1 C1 (nolock),                  
      MSAJAG..Client2 C2 (nolock)                  
WHERE x.DrCr = 'D'                  
    AND x.BDpId  =@BDPID               
    AND x.BCltDpId = @BCltDpId   
   -- AND x.transdate >=  @TRANSDATE_FROM AND transdate <=  @TRANSDATE_TO + ' 23:59'         
    AND x.transdate >=  @TRANSDATE_FROM AND transdate <=  @TRANSDATE_TO + ' 23:59'           
   -- AND left(x.transdate,11) = @Tdate                
    AND x.Filler2 = 1                  
    --AND x.Party_Code >= @FParty                  
    --AND x.Party_Code <= @TParty                  
    --AND x.Scrip_Cd >= @FScrip                  
    --AND x.Scrip_Cd <= @TScrip                  
    AND C2.Party_Code = x.Party_Code                  
    AND C2.Cl_Code = C1.Cl_Code                  
                       
Insert Into #Deltrans_report                  
Select                  
    x.SNo,                  
    x.Sett_No,                  
    x.Sett_Type,                  
    x.Tcode,                  
    x.Trtype,                  
    x.Party_Code,                  
    c1.Short_Name,                  
    x.Scrip_Cd,                  
    x.Series,                  
    x.Qty,                  
    x.Fromno,                  
    x.Certno,                  
    x.Reason,                  
    x.Drcr,                  
    x.Delivered,                  
    x.Dptype,                  
    x.Dpid,                  
    x.Cltdpid,                  
    x.Isett_No,                  
    x.Isett_Type,              
    x.Sharetype,                  
    x.Transdate,                  
    x.Filler2,                  
    x.Bdptype,                  
    x.Bdpid,              
    x.Bcltdpid,                  
 x.HolderName                  
from                  
      MSAJAG..Deltrans_report x (Nolock) ,                  
      MSAJAG..Client1 C1 (nolock),              
     MSAJAG..Client2 C2 (nolock)                  
WHERE x.DrCr = 'D'                  
    AND (HolderName like 'Ben %'                  
 Or HolderName like 'Mar%')                  
    AND x.BDpId  =@BDPID               
    AND x.BCltDpId = @BCltDpId  
   -- AND x.transdate >=  @TRANSDATE_FROM AND transdate <=  @TRANSDATE_TO + ' 23:59'         
    AND x.transdate >=  @TRANSDATE_FROM AND transdate <=  @TRANSDATE_TO + ' 23:59'           
  --  AND left(x.transdate,11) = @Tdate                    
    --AND x.Party_Code >= @FParty                  
    --AND x.Party_Code <= @TParty                  
    --AND x.Scrip_Cd >= @FScrip                  
    --AND x.Scrip_Cd <= @TScrip                  
    AND C2.Party_Code = x.Party_Code                  
    AND C2.Cl_Code = C1.Cl_Code                  
    And Delivered = 'G'                  
                 
                 
/* Pool To Client Trans */                      
                   
SELECT                  
    D.Sett_no,                  
    D.Sett_Type,                  
    D.Party_Code,                  
    D.Short_Name,                  
    D.Scrip_CD,                  
    D.Series,                  
    Qty=Sum(D.Qty),                  
    D.CertNo,                  
    D.FromNo,                  
    D.DpType,                  
    D.DpId,                  
    D.CltDpId,                  
    D.ISett_No,                  
    D.ISett_Type,D.Transdate, D.Bdpid,                  
    D.Bcltdpid,                  
    Remark='Pay-Out'          
      
INTO #TO_CHECK      
FROM #Deltrans_report D (nolock),                  
    MSAJAG..DeliveryDp DP (nolock)                  
WHERE D.Delivered = 'D'                  
    AND D.BDpId = Dp.DpId  
    AND D.BCltDpId = Dp.DpCltNo                  
    AND D.TrType In ( 904 , 905 )                  
    AND Dp.Description Like '%POOL%'                  
--  And Filler2 = 1            
    AND DP.DpId =@BDPID         AND DP.DpCltNo = @BCltDpId                    
GROUP BY D.Sett_no,                  
    D.Sett_Type,                  
    D.Party_Code,                  
    D.Short_Name,                  
    D.Scrip_CD,                  
    D.Series,                  
    D.CertNo,                  
    D.FromNo,       D.Transdate, D.Bdpid,                  
    D.Bcltdpid,          
    D.DpType,                  
    D.DpId,                  
    D.CltDpId,                  
    D.ISett_No,                  
    D.ISett_Type                  
                 
Union All                      
                 
SELECT                  
    D.Sett_no,                  
    D.Sett_Type,                  
    D.Party_Code,                  
    D.Short_Name,                  
    D.Scrip_CD,                  
    D.Series,                  
    Qty=Sum(D.Qty),                  
    D.CertNo,                  
    D.FromNo,                  
    D.DpType,                  
    D.DpId,                  
    D.CltDpId,                  
    D.ISett_No,          
    D.ISett_Type,  D.Transdate, D.Bdpid,                  
    D.Bcltdpid,                
    Remark='Pay-Out'                  
FROM #Deltrans_report D (nolock),                  
    MSAJAG..DeliveryDp DP (nolock),                  
    MSAJAG..DeltranSTemp DT (nolock)                  
WHERE D.Delivered = 'G'                  
    AND D.BDpId = Dp.DpId                  
    AND D.BCltDpId = Dp.DpCltNo                  
    AND D.TrType In ( 904 , 905 )                  
    AND Dp.Description Like '%POOL%'                  
--  And d.Filler2 = 1                
    AND DP.DpId = @BDPID               
    AND DP.DpCltNo = @BCltDpId                    
    AND D.SNo = DT.Sno                  
    AND DT.BDpId  =@BDPID                 
    AND DT.BCltDpId = @BCltDpId                   
    AND DT.transdate >=  @TRANSDATE_FROM AND DT.TransDate <=  @TRANSDATE_TO + ' 23:59'      
GROUP BY D.Sett_no,                  
    D.Sett_Type,                  
    D.Party_Code,                  
    D.Short_Name,                 
    D.Scrip_CD,                  
    D.Series,                  
    D.CertNo,                  
    D.FromNo,                  
    D.DpType,       D.Transdate, D.Bdpid,                  
    D.Bcltdpid,          
    D.DpId,                  
    D.CltDpId,                  
  D.ISett_No,                  
    D.ISett_Type                  
                 
Union All                      
/* Pool To Pool InterSett */                      
SELECT                  
    D.Sett_no,                  
    D.Sett_Type,                  
    D.Party_Code,                  
    D.Short_Name,                  
    D.Scrip_CD,                  
    D.Series,                  
    Qty=Sum(D.Qty),                  
    D.CertNo,                  
    D.FromNo,                  
D.DpType,                  
    D.DpId,                  
    D.CltDpId,                  
    D.ISett_No,                  
    D.ISett_Type,   D.Transdate, D.Bdpid,                  
    D.Bcltdpid,              
    Remark='Inter Sett'                  
FROM #Deltrans_report D (nolock),                  
    MSAJAG..DeliveryDp DP (nolock)                  
WHERE D.Delivered in ('G','D')                  
    AND D.BDpId = Dp.DpId                  
    AND D.BCltDpId = Dp.DpCltNo                  
    AND D.TrType = 907                  
    AND Dp.Description Like '%POOL%'                  
--  And Filler2 = 1            
    AND DP.DpId = @BDPID               
    AND DP.DpCltNo = @BCltDpId                    
GROUP BY D.Sett_no,                  
  D.Sett_Type,                  
    D.Party_Code,                  
    D.Short_Name,                  
    D.Scrip_CD,                  
    D.Series,                  
    D.CertNo,                  
    D.FromNo,                  
    D.DpType,    D.Transdate, D.Bdpid,                  
    D.Bcltdpid,              
    D.DpId,                  
    D.CltDpId,                  
    D.ISett_No,                  
    D.ISett_Type                  
                 
Union All                      
/* Pool To Ben Trans */                      
SELECT                  
    D.Sett_no,                  
    D.Sett_Type,                  
    D.Party_Code,                  
    D.Short_Name,                  
    D.Scrip_CD,                  
    D.Series,                  
    Qty=Sum(D.Qty),                  
    D.CertNo,                  
    D.FromNo,                  
    D.DpType,                  
    DP1.DpId,                  
    CltDpId=DP1.DpCltNo,                  
    D.ISett_No,                  
    D.ISett_Type, D.Transdate, D.Bdpid,                  
    D.Bcltdpid,                
    Remark='Pool To Ben'                  
FROM #Deltrans_report D (nolock),                  
    MSAJAG..DeliveryDp DP (nolock),                  
   MSAJAG.. DeliveryDp DP1 (nolock)                  
