-- Exec RPT_DELBENPAYOUT_Pledge 'CDSL','12033200','1203320006951435','0','ZZZZZZ','0','ZZZZZZ','','INVOKE', 1, 0, 'All', 'All'
-- Exec RPT_DELBENPAYOUT_Pledge 'CDSL','12033200','1203320006951435','0','ZZZZZZ','0','ZZZZZZ','','Invoke', 0, 0, 'All', 'All'


CREATE PROC [dbo].[RPT_DELBENPAYOUT_UNPLDEPN_BKP28012026] (          
@BDPTYPE VARCHAR(4), @BDPID VARCHAR(8), @BCLTDPID VARCHAR(16),           
@FROMPARTY VARCHAR(10), @TOPARTY VARCHAR(10),          
@FROMSCRIP VARCHAR(10), @TOSCRIP VARCHAR(10),          
@ACNAME VARCHAR(100),          
@CHKFLAG VARCHAR(20), @PAYFLAG INT, @SUMMARYFLAG INT,    
@CATEGORY VARCHAR(20), @BRANCHCODE VARCHAR(10),
@SETTNO VARCHAR(7), @SETTTYPE VARCHAR(2))          
AS               

DECLARE @CUSAFLAG  INT

SET @CUSAFLAG = 0 

SELECT  @CUSAFLAG = (CASE WHEN DESCRIPTION LIKE '%CUSPA%' THEN 1 ELSE 0 END)
FROM DELIVERYDP WHERE DPID = @BDPID AND DPCLTNO = @BCLTDPID
SET @CUSAFLAG = 1          
IF @PAYFLAG = 1          
BEGIN  
     SELECT D.SCRIP_CD,SERIES=D.SCRIP_CD,D.PARTY_CODE,LONG_NAME=ISNULL(INTRODUCER,' '),TRTYPE,          
	 D.DPTYPE,CLTDPID,D.DPID,CERTNO,QTY=SUM(QTY),BDPTYPE,BDPID,BCLTDPID,AMOUNT=1,          
     ISETT_NO=CONVERT(VARCHAR(13),(CASE WHEN @CUSAFLAG = 1 THEN ISETT_NO ELSE '' END)),
	 ISETT_TYPE=(CASE WHEN @CUSAFLAG = 1 THEN ISETT_TYPE ELSE '' END),
     SETT_NO= '',
     SETT_TYPE='',
     SERIESID=D.SERIES,CL_RATE = CONVERT(NUMERIC(18,4),0),
     PLDNO = FROMNO, 
     AGGRNO = CONVERT(VARCHAR(20),D.PARTY_CODE),
     CLDATE = (CASE WHEN D.DPTYPE = 'CDSL' THEN '31123050' ELSE '30501231' END),
     UCC_CODE = D.Party_Code,
     DataFlag = 0,
     PartCounter = 0,
     OrgDpId = Convert(Varchar(8),''),
	 EPN_ACCOUNT=CONVERT(VARCHAR(16),''),
	 ENTITY_IDENTITY=CONVERT(VARCHAR(2),'TM'),UCCCODE=CONVERT(VARCHAR(15),LTRIM(RTRIM(UPPER(D.PARTY_CODE)))),
	 SEG_ID=CONVERT(VARCHAR(2),''),UCC_CMID=CONVERT(VARCHAR(11),''),UCC_TMID=CONVERT(VARCHAR(10),''),UCC_EXID=CONVERT(VARCHAR(10),'') 
     INTO #DELPAYOUT_1           
     FROM MULTICLTID M, DELTRANS D         
     WHERE TRTYPE = (case when @CHKFLAG = 'INVOKE' THEN 1003 ELSE 905 END) 
	 AND D.PARTY_CODE = M.PARTY_CODE 
	 AND M.DPID = D.DPID AND M.CLTDPNO = D.CLTDPID           
     AND M.DPTYPE = D.DPTYPE 
	 AND ISETT_NO = CASE WHEN @SETTNO = '' THEN ISETT_NO ELSE @SETTNO END
	 AND ISETT_TYPE = CASE WHEN @SETTTYPE = '' THEN ISETT_TYPE ELSE @SETTTYPE END 
	 AND DELIVERED = '0' 
	 AND D.PARTY_CODE <> 'BROKER'           
     AND BDPTYPE = @BDPTYPE AND BDPID = @BDPID          
     AND BCLTDPID = @BCLTDPID AND DRCR = 'D' AND FILLER2 = 1           
     AND D.PARTY_CODE >= @FROMPARTY AND D.PARTY_CODE <= @TOPARTY          
     AND D.SCRIP_CD >= @FROMSCRIP AND D.SCRIP_CD <= @TOSCRIP        
	 AND TRTYPE >= (CASE WHEN @CHKFLAG <> 'INVOKE'        
        THEN 905        
        WHEN @CHKFLAG = 'INVOKE'  
        THEN 1003  
        END)               
     GROUP BY  
     D.SCRIP_CD,D.SERIES,D.PARTY_CODE,ISNULL(INTRODUCER,' '),TRTYPE,CLTDPID,D.DPID,          
     CERTNO, BDPTYPE,BDPID,BCLTDPID,D.DPTYPE,FROMNO,
	 (CASE WHEN @CUSAFLAG = 1 THEN ISETT_NO ELSE '' END),
	 (CASE WHEN @CUSAFLAG = 1 THEN ISETT_TYPE ELSE '' END)          
     ORDER BY D.DPTYPE,D.PARTY_CODE,D.SCRIP_CD          
     
	
	IF @BDPTYPE = 'CDSL' AND @CUSAFLAG = 1
	BEGIN  	
		/*
		UPDATE #BEN_DATA SET UCCCODE = UPPER(LTRIM(RTRIM(U.UCC_CODE))) FROM UCC_CLIENT U
		WHERE U.PARTY_CODE = #BEN_DATA.PARTY_CODE
		AND ISNULL(U.UCC_CODE,'') <> ''
		*/
		UPDATE #DELPAYOUT_1 SET 
		SEG_ID	= C.SEG_ID, 
		ENTITY_IDENTITY = C.ENTITY_IDENTITY,
		UCC_CMID = C.UCC_CMID,
		UCC_TMID = C.UCC_TMID,
		UCC_EXID = C.UCC_EXID
		FROM TBL_SEGMENT_CDSL C 
		WHERE C.SETT_TYPE = ISETT_TYPE

		UPDATE #DELPAYOUT_1 SET EPN_ACCOUNT = DPCLTNO FROM DELIVERYDP WHERE DESCRIPTION LIKE '%EAR%'
		AND SEGMENT = 'CAPITAL'
		AND EXCHANGE = 'NSE'
		AND ISETT_TYPE IN (SELECT SETT_TYPE FROM DEL_MARKETTYPE WHERE EXCHANGE = 'NSE')
		
		UPDATE #DELPAYOUT_1 SET EPN_ACCOUNT = DPCLTNO FROM DELIVERYDP WHERE DESCRIPTION LIKE '%EAR%'
		AND SEGMENT = 'CAPITAL'
		AND EXCHANGE = 'BSE'
		AND ISETT_TYPE IN (SELECT SETT_TYPE FROM DEL_MARKETTYPE WHERE EXCHANGE = 'BSE')
				
		UPDATE #DELPAYOUT_1 SET ISETT_NO = CDSL_SETT_TYPE 
										   + (CASE WHEN IS_SETT_CHANGE = 1 
												 THEN CONVERT(VARCHAR,Left(Right(ISETT_NO, 5), 2)) 
													  + RIGHT('00' + CONVERT(VARCHAR, CONVERT(INT, LEFT(RIGHT(ISETT_NO, 5), 2)) + 1), 2) 
													  + RIGHT(ISETT_NO, 3)  
												 ELSE ISETT_NO
											END)
		FROM DEL_MARKETTYPE 
		WHERE ISETT_TYPE = DEL_MARKETTYPE.SETT_TYPE
	END
	
	IF @BDPTYPE = 'NSDL' AND @CUSAFLAG = 1
	BEGIN  	
		/*
		UPDATE #BEN_DATA SET UCCCODE = UPPER(LTRIM(RTRIM(U.UCC_CODE))) FROM UCC_CLIENT U
		WHERE U.PARTY_CODE = #BEN_DATA.PARTY_CODE
		AND ISNULL(U.UCC_CODE,'') <> ''
		*/
		UPDATE #DELPAYOUT_1 SET 
		SEG_ID	= C.SEG_ID, 
		--ENTITY_IDENTITY = C.ENTITY_IDENTITY,
		--UCC_CMID = C.UCC_CMID,
		UCC_TMID = C.TMCODE,
		UCC_EXID = C.EXCG_ID
		FROM TBL_SEGMENT_NSDL C 
		WHERE C.SETT_TYPE = ISETT_TYPE

		UPDATE #DELPAYOUT_1 SET EPN_ACCOUNT = NSECMBPID FROM DELSEGMENT 
		WHERE ISETT_TYPE IN (SELECT SETT_TYPE FROM DEL_MARKETTYPE WHERE EXCHANGE = 'NSE')
		
		UPDATE #DELPAYOUT_1 SET EPN_ACCOUNT = BSECMBPID FROM DELSEGMENT 
		WHERE ISETT_TYPE IN (SELECT SETT_TYPE FROM DEL_MARKETTYPE WHERE EXCHANGE = 'BSE')
				
		UPDATE #DELPAYOUT_1 SET ISETT_NO = NSDL_MARKETTYPE 
										   + (CASE WHEN IS_SETT_CHANGE = 1 
												 THEN CONVERT(VARCHAR,Left(Right(ISETT_NO, 5), 2)) 
													  + RIGHT('00' + CONVERT(VARCHAR, CONVERT(INT, LEFT(RIGHT(ISETT_NO, 5), 2)) + 1), 2) 
													  + RIGHT(ISETT_NO, 3)  
												 ELSE ISETT_NO
											END)
		FROM DEL_MARKETTYPE 
		WHERE ISETT_TYPE = DEL_MARKETTYPE.SETT_TYPE
	END
	/*
    SELECT * INTO #PARTCNT FROM TBL_MARGIN_PLEDGE P
    WHERE BDPTYPE = @BDPTYPE AND BDPID = @BDPID  AND BCLTACCNO = @BCLTDPID 
    AND TRDATE <= GETDATE() AND TRANSNO = PARENTPSN_NO       
    AND ISIN IN (SELECT CERTNO FROM #DELPAYOUT_1 D
                   WHERE D.PLDNO = TRANSNO AND CertNo = Isin 
                    AND D.DpId = P.DPID AND D.CltDpId = P.CLTACCNO)
     
     UPDATE #DELPAYOUT_1 SET PartCounter = RECCNT 
     FROM (SELECT TRANSNO, ISIN, DPID, CLTACCNO, RECCNT = ISNULL(COUNT(1),0) FROM #PARTCNT
            GROUP BY TRANSNO, ISIN, DPID, CLTACCNO ) P
     WHERE #DELPAYOUT_1.PLDNO = TRANSNO AND CertNo = Isin 
     AND #DELPAYOUT_1.DpId = P.DPID AND #DELPAYOUT_1.CltDpId = P.CLTACCNO
     */
     UPDATE #DELPAYOUT_1 SET UCC_CODE = ISNULL(U.UCC_CODE,#DELPAYOUT_1.UCC_CODE)
     FROM UCC_CLIENT U
     WHERE #DELPAYOUT_1.PARTY_CODE = U.PARTY_CODE
     AND  ISNULL(U.UCC_CODE,'') <> ''

     UPDATE #DELPAYOUT_1 SET CL_RATE = C.CL_RATE FROM CLOSING C WHERE                             
     SYSDATE = (SELECT MAX(SYSDATE) FROM CLOSING C1 WHERE C1.SCRIP_CD = #DELPAYOUT_1.SCRIP_CD                            
     AND C1.SERIES IN ('EQ','BE') AND #DELPAYOUT_1.SERIESID IN ('EQ','BE')                          
     AND SYSDATE <= LEFT(GETDATE(),11) + ' 23:59' )                      
     AND C.SCRIP_CD = #DELPAYOUT_1.SCRIP_CD                            
     AND #DELPAYOUT_1.SERIESID IN ('EQ','BE')           
     AND C.SERIES IN ('EQ','BE')                           
     AND #DELPAYOUT_1.CL_RATE = 0          
          
     UPDATE #DELPAYOUT_1 SET CL_RATE = C.CL_RATE FROM CLOSING C WHERE                             
     SYSDATE = (SELECT MAX(SYSDATE) FROM CLOSING C1 WHERE C1.SCRIP_CD = #DELPAYOUT_1.SCRIP_CD                            
     AND C1.SERIES = #DELPAYOUT_1.SERIESID      
     AND SYSDATE <= LEFT(GETDATE(),11) + ' 23:59' )                      
     AND C.SCRIP_CD = #DELPAYOUT_1.SCRIP_CD                            
     AND C.SERIES = #DELPAYOUT_1.SERIESID            
     AND #DELPAYOUT_1.CL_RATE = 0          
      
	  
	 --UPDATE DL SET UCC_TMID = P.TM_CODE FROM  #DELPAYOUT_1 DL, TBL_MARGIN_PLEDGE P WHERE  DL.UCC_CODE = P.UCC_CODE    
	SELECT * FROM #DELPAYOUT_1           
END

GO

