    INSERT INTO ANGELCOMMODITY.ACCOUNTMCDX.DBO.TBL_AUTO_JV_UPLOAD              
    (SRNO,VVDT,EEDT,CLTCODE,DRCR,AMOUNT,NARRATION,BRANCHCODE,L1_SRNO,REFNO,POSTFLAG)              
    SELECT               
     @RECNO,              
     VVDT=GETDATE(),              
     EEDT=GETDATE(),              
     D.PARTY_CODE,              
     DRCR = (CASE WHEN PARTY_CODE = 'Y04803' AND SUM(ROUND(AMOUNT,2)) < 0 THEN 'D' ELSE 'C' END),             
     NETAMOUNT = (CASE WHEN PARTY_CODE = 'Y04803' THEN ABS(SUM(ROUND(NETAMOUNT,2))) ELSE SUM(ROUND(NETAMOUNT,2)) END),              
     NARRATION = 'BEING DIVIDEND @ ' + CONVERT(VARCHAR,CONVERT(NUMERIC(18,2),RATE)) + ' FOR SCRIP ' + SCRIP_NAME + ' WITH QTY '               
         + CONVERT(VARCHAR,SUM(NEWQTY)) + ' IN BOID ' + D.CLTDPID,               
     BRANCHCODE = 'ALL',              
     L1_SRNO = @RECNO,              
     REFNO = @REFNO,              
     POSTFLAG = 0               
    FROM #TBL_CORPACT_DATA D             
    WHERE RECORD_DATE    = CONVERT(VARCHAR(11), CONVERT(DATETIME, @RECORDDATE, 103), 109)              
    AND   ISIN           = @OLDISIN              
    AND   D.DPID       = LEFT(@BOID,8)              
    AND   RIGHT(D.CLTDPID,8)= RIGHT(@BOID,8)              
    AND   TYPE_CP_ACTION = @TYPE_CP_ACTION              
    AND   INTERNAL_TYPE  = @INTERNAL_TYPE              
    AND   PARTY_CODE  = (CASE WHEN @LEVEL = 1 THEN PARTY_CODE ELSE @PARTY_CODE END)              
    AND PROCESS_STATUS = 3              
    AND D.NEWSEGMENT = 'FUTURES'           
    AND D.NEWEXCHANGE = 'MCX'           
    GROUP BY PARTY_CODE,ISIN,D.CLTDPID,INTERNAL_TYPE,RATE,SCRIP_NAME    
      
