CREATE PROCEDURE USP_MTF_PLEDGE_DATA (@EXEDATE Datetime)
AS
--Created and Altered by Akshay Marathe on 10th OCT 2023
SELECT * INTO #TBL_POA_PLD_ADNL FROM MSAJAG..TBL_POA_PLD_ADNL WHERE EXEDATE = @EXEDATE

---STEP 2 COLLECT DATA FOR SHARES RECEIVED FOR PLEDGE IN MTF ACCOUNT

SELECT * INTO #MTFPLEDGE FROM MSAJAG..DELTRANS WHERE TRANSDATE = @EXEDATE AND DRCR='C' AND BCLTDPID ='1203320030135829'
AND PARTY_CODE NOT IN ('BROKER','EXE','NSE') AND FILLER2 ='1' AND BRANCHCD <> 'INVOKE'

--------------PART OF STEP 2 GETTNIG THE SUM------

SELECT PARTY_CODE,SCRIP_CD,SERIES,CERTNO,SUM(QTY) AS MTFPLDGQTY,BDPID,BCLTDPID,DPID,CLTDPID,'BTC' AS FILLER,TCODE INTO #MTFPLEDGE_FINAL 
FROM #MTFPLEDGE GROUP BY PARTY_CODE,SCRIP_CD,SERIES,CERTNO,BDPID,BCLTDPID,DPID,CLTDPID,TCODE

 --------------MATCHING DATA OF STEP1 AND STEP 2 AND FINDING THE EXCESS

SELECT A.PARTY_CODE,A.CERTNO,A.SCRIP_CD,A.CLTDPID,A.QTY,B.PARTY_CODE AS MTFCODE,B.CERTNO AS ISIN,B.MTFPLDGQTY,B.BCLTDPID
INTO #MTFSTOCK
FROM #TBL_POA_PLD_ADNL A,#MTFPLEDGE_FINAL B
WHERE A.PARTY_CODE=B.PARTY_CODE AND A.CERTNO=B.CERTNO

------------------IDENTIFY DATA WHERE OTP WAS RECEIVED LATE AND MTF PLEDGE HAPPENED IN CLIENT ACCOUNT


SELECT * INTO #EXCESSMTF FROM #MTFPLEDGE_FINAL WHERE PARTY_CODE+CERTNO NOT IN (SELECT MTFCODE+ISIN FROM #MTFSTOCK)
 
---STEP 3 --------QUERY FOR IDENTIFYING PAYIN DONE FROM EXCESS SHARES PLEDGE


SELECT * INTO #PAYINFROMMTF FROM MSAJAG..DELTRANS WHERE  FILLER2 ='1' AND DRCR='D' AND DELIVERED IN ('G','D')  AND BCLTDPID ='1203320030135829'
AND TRANSDATE = @EXEDATE

 
 ------OUTPUT OF PAYIN DATA-----
 

--SELECT A.*,ISNULL(B.QTY,0) AS PAYIN_QTY,ISNULL(B.FROMNO,'') AS FROMNO FROM #EXCESSMTF A
--, #PAYINFROMMTF B WHERE A.PARTY_CODE=B.PARTY_CODE AND A.CERTNO=B.CERTNO AND A.TCODE=B.TCODE

SELECT A.*,ISNULL(B.QTY,0) AS PAYIN_QTY_FROMMTF,ISNULL(B.FROMNO,'') AS FROMNO FROM #EXCESSMTF A
LEFT OUTER JOIN #PAYINFROMMTF B ON (A.PARTY_CODE=B.PARTY_CODE AND A.CERTNO=B.CERTNO AND A.TCODE=B.TCODE)
ORDER BY 1

DROP TABLE #TBL_POA_PLD_ADNL
DROP TABLE #MTFPLEDGE_FINAL
DROP TABLE #MTFPLEDGE
DROP TABLE #MTFSTOCK
DROP TABLE #PAYINFROMMTF
DROP TABLE #EXCESSMTF

GO

