-- PROCEDURE dbo.USP_RPT_EARLY_PAYIN_MIS
-- --------------------------------------------------
-- // RUN AT 232 USE DUSTBIN
--// REQUEST BY PARESH NATEKAR / ANIL WANDRE
--// REPORT NAME:- EARLY PAYIN MIS REPORT

CREATE PROC [dbo].[USP_RPT_EARLY_PAYIN_MIS] (@SETT_NO VARCHAR(10))
AS

--DECLARE @SETT_NO VARCHAR(10)
--SET @SETT_NO='2024071'
BEGIN

IF OBJECT_ID(N'tempdb..#Payin') IS NOT NULL                          
DROP TABLE #Payin 
Select Party_code, Scrip_cd,Series,Qty,I_ISIN into #Payin  from MSAJAG.DBO.deliveryclt (nolock) where sett_no =@Sett_No and inout ='I'

IF OBJECT_ID(N'tempdb..#recd') IS NOT NULL                          
DROP TABLE #recd 
Select Party_code, Scrip_cd,series,Certno,Sum(qty) as Qty into #recd   from MSAJAG.DBO.Deltrans (nolock)
where sett_no =@Sett_No and drcr ='C' and filler2=1   and party_code not in ('EXE','Broker','NSE')
Group by Party_code, Scrip_cd,series,Certno

IF OBJECT_ID(N'tempdb..#temp') IS NOT NULL                          
DROP TABLE #temp 
Create table #temp (Party_code varchar(10), Scrip_cd varchar(10),Series varchar(10),Certno varchar(20),Sell_Obligation int,Recd int,Pending int ,Payin_Status varchar(10),cl_rate numeric(18,4))

Insert into #temp
Select Distinct Party_code, Scrip_cd,series,Certno,0,0,0,'',0 from (
Select Distinct Party_code, Scrip_cd,series,Certno from #recd
union all
Select Distinct Party_code, Scrip_cd,series,I_ISIN from #Payin )a

Update #temp
Set Sell_Obligation =Qty
FROM #Payin Where #temp.Party_code =#Payin.Party_code and #temp.Certno=I_ISIN

Update #temp
Set Recd =Qty
FROM #recd Where #temp.Party_code =#recd.Party_code and #temp.Certno=#recd.Certno

Declare @trdate datetime,@cldate datetime
Select @trdate=Start_date from MSAJAG.DBO.sett_mst where sett_no =@Sett_No and sett_type='M'
Select @cldate=max(start_date) from MSAJAG.DBO.sett_mst where Start_date <@trdate and sett_type='M'

IF OBJECT_ID(N'tempdb..#clrate') IS NOT NULL                          
DROP TABLE #clrate 
Select * into #clrate from angelnsecm.msajag.dbo.closing_mtm  where SYSDATE =@cldate

 Update #temp
Set cl_rate =#clrate.Cl_rate
FROM #clrate Where  #temp.Certno=#clrate.isin and #temp.Scrip_cd =#clrate.Scrip_Cd and #clrate.Series =#temp.Series
 Update #temp
Set cl_rate =#clrate.Cl_rate
FROM #clrate Where  #temp.Certno=#clrate.isin and #temp.Scrip_cd =#clrate.Scrip_Cd and #clrate.series in ('BE','EQ') and #temp.cl_rate='0.0000'
Update #temp set Pending=Sell_Obligation -Recd

--SELECT * FROM #TEMP

----// MAIN DATA
Select @SETT_NO AS 'SETT_NO', Obligation='Total Obligation', No_of_Shares=Sum(Sell_Obligation) ,Obligation_Value=ROUND(Sum(Sell_Obligation*cl_rate)/10000000,2), no_of_Scrips=Count(distinct scrip_cd),No_of_Clients=Count(distinct party_code)
from #temp   where  Sell_Obligation <>0
Union all
Select @SETT_NO AS 'SETT_NO', Obligation='Obligation Done', Sell_Obligation=Sum(case when Sell_Obligation > recd Then recd else Sell_Obligation end) ,
ROUND(Sum((case when Sell_Obligation > recd Then recd else Sell_Obligation end) *cl_rate)/10000000,2),Count(distinct scrip_cd),Count(distinct party_code)
from #temp   where  Sell_Obligation <>0   and recd <> 0
Union all
Select @SETT_NO AS 'SETT_NO', Obligation='NO Obligation', Sell_Obligation=Sum(Recd) ,ROUND(Sum(Recd*cl_rate)/10000000,2),Count(distinct scrip_cd),Count(distinct party_code)
from #temp   where  Sell_Obligation =0
Union all
Select @SETT_NO AS 'SETT_NO', Obligation='Excess Obligation', Sell_Obligation= ABS(Sum(Pending)) ,ROUND(ABS(Sum(Pending*cl_rate))/10000000,2),Count(distinct scrip_cd),Count(distinct party_code)
from #temp   where  Sell_Obligation <>0 and pending <0
 Union all
Select @SETT_NO AS 'SETT_NO', Obligation='Pending Obligation', Sell_Obligation= ABS(Sum(Pending)) ,ROUND(ABS(Sum(Pending*cl_rate))/10000000,2),Count(distinct scrip_cd),Count(distinct party_code)
from #temp   where  Sell_Obligation <>0 and pending >0

END

GO

