CREATE function  [dbo].[fun_SBperformanceTest](@access_to varchar(30),@access_code varchar(30),@sbcode varchar(30))        
         
RETURNS   @SBPerformanceDetails  table        
(          
Sub_broker varchar(30),          
TotalClient int,          
NewClient int,          
TradingDays int,          
TillDateRevenue money,          
MonthlyRevenue money,          
DailyRevenue money,          
TotalTradedClients int          
)          
BEGIN        
        
        
        
 -- Fill the table variable with the rows for your result set        
       
set @sbcode=@sbcode        
Declare @Sdate datetime,@Edate datetime,@tradingdays int          
          
select @Sdate=Convert(datetime,CONVERT(VARCHAR(11),DATEADD(dd,-(DAY(getdate())-1),getdate()),103),103)          
select @Edate=  Convert(datetime,CONVERT(VARCHAR(11),GETDATE(),103)+ ' 23:59:59',103)          
          
select @tradingdays=COUNT(distinct sauda_date) from mis.remisior.dbo.comb_cli with (nolock) where sauda_date between @Sdate and @Edate          
and segment in ('ACDLNSX','ACDLFO','ABLCM','ACPLMCD','ACDLCM')      
        
         
insert into @SBPerformanceDetails(Sub_broker,TotalClient,NewClient,TradingDays)        
          
select sub_broker,TotalClient=COUNT(party_code),NewClient=sum(case when First_Active_date>= @Sdate then 1 else 0 end)               
,@tradingdays        
from INTRANET.RISK.DBO.client_details with (nolock) where 
sub_broker=@sbcode  and    First_Active_date<=GETDATE()     
group by sub_broker          
          
  
Declare @TradedClients int=0;  
set @TradedClients=(       
select total_tradedclient=sum(party_codecnt1)from   
(  
 select sauda_date,subbrokcode,party_codecnt=COUNT(party_code),party_codecnt1=COUNT(distinct party_code)  from           
 mis.remisior.dbo.comb_cli with (nolock) where subbrokcode=@sbcode  and  sauda_date >=@Sdate       
 group by sauda_date,subbrokcode  
)x  
group by subbrokcode  
)  
  
  
  
    
update a set           
TillDateRevenue=b.TotalRevenue,          
MonthlyRevenue=b.MonthRevenue,          
TotalTradedClients=@TradedClients          
from           
@SBPerformanceDetails a inner join           
(select subbrokcode,          
TotalRevenue=0.00,          
MonthRevenue=Convert(decimal(15,2),SUM( isnull(brok_earned,0)))          
from mis.remisior.dbo.comb_cli with (nolock) where subbrokcode=@sbcode  and  sauda_date>=@Sdate       
group by subbrokcode    
    
    
    
)b on a.Sub_broker=b.subbrokcode          
          
update @SBPerformanceDetails set dailyRevenue=Convert(decimal(15,2),(MonthlyRevenue/TradingDays))          
        
--select * from @SBPerformanceDetails        
        
RETURN         
END

