-- FUNCTION dbo.ufn_GetDaysInMonth
-- --------------------------------------------------



CREATE FUNCTION [dbo].[ufn_GetDaysInMonth] ( @pDate   int ,@year int)
RETURNS INT
AS
BEGIN

    RETURN CASE WHEN @pDate IN (1, 3, 5, 7, 8, 10, 12) THEN 31
                WHEN @pDate IN (4, 6, 9, 11) THEN 30
                ELSE CASE WHEN (@year% 4    = 0 AND
                                @year % 100 != 0) OR
                               (@year % 400  = 0)
                          THEN 29
                          ELSE 28
                     END
           END

END

