-- --------------------------------------------------
-- =============================================
-- Author:		Shravan Yadav 
-- Create date: 2020-02-27
-- Description:	To create tiny url for sending in sms
-- =============================================
--EXEC spx_Campaign_GetTinyUrl 'https://dra.angelbackoffice.com/diykyc/SubbrokerLead?SbTag=U0lMQw==&BType=VkJC'
--EXEC spx_Campaign_GetTinyUrl 'https://dra.angelbackoffice.com/diykyc/SubbrokerLead?SbTag=U0lMQw==&BType=SVRQ'
--EXEC spx_Campaign_GetTinyUrl 'https://dra.angelbackoffice.com/diykyc/SubbrokerLead?SbTag=U0lMQw==&BType=T0xE'
CREATE PROC [dbo].[spx_Campaign_GetTinyUrl]
	@url varchar(500) 
as
begin

	Declare @obj int 
	Declare @tinyurl varchar(max) = '';
	Declare @response varchar(500);
	
	set @tinyurl = 'http://tinyurl.com/api-create.php?url=' + @url;
	
	EXEC sp_OACREATE 'MSXML2.ServerXMLHttp', @obj out
	EXEC sp_OAMethod @obj, 'Open', NULL, 'GET', @tinyurl, false
	EXEC sp_OAMethod @obj, 'send'
	EXEC sp_OAGetProperty @obj , 'responseText', @response out 
	
	select @response 'TinyUrl'
end

GO

