			IF(@MICROSITE>0)
			BEGIN
			---SEND MAIL
			SELECT @MAILBODY=DBO.UDP_B2BMAILBODY(@MEETINGID,@ID1,'MICROSITE')
			SELECT 1
			SET @FLAG=1
			END
            
			IF( @FLAG=0)
			BEGIN
