create PROCEDURE [dbo].[USP_CATEGORY]
	@PROCESS AS VARCHAR(100),
	@DATA1 AS VARCHAR(100)='',
	@DATA2 AS VARCHAR(100)='',
	@DATA3 AS VARCHAR(100)='',
	@DATA4 AS VARCHAR(100)='',
	@DATA5 AS VARCHAR(100)='',
	@DATA6 AS VARCHAR(100)='',
	@DATA7 AS VARCHAR(100)='',
	@DATA8 AS VARCHAR(100)='',
	@DATA9 AS VARCHAR(100)='',
	@DATA10 AS VARCHAR(100)=''
AS
BEGIN
	DECLARE @CNT AS INT=0
	IF(@PROCESS='INSERTFILE')
	BEGIN
		SELECT @CNT =COUNT(1) FROM TBL_CATEGORY WHERE CATEGORY = @DATA1 and  isdelete = 0
		IF(@CNT=0)
		BEGIN
			INSERT INTO TBL_CATEGORY(CATEGORY,URL,ADDEDBY,ADDEDON,MODIFIEDBY,MODIFIEDON)
			VALUES(@DATA1,@DATA2,@DATA3,GETDATE(),'','')
			SELECT 'File Inserted Successfully'
		END
		ELSE
		BEGIN
			UPDATE TBL_CATEGORY 
			SET URL=@DATA2,
			MODIFIEDBY = @DATA3,
			MODIFIEDON = GETDATE()
			WHERE CATEGORY = @DATA1
			SELECT 'File Updated Successfully'
		END
	END
	
	
	
	IF(@PROCESS='DeleteFile')
	BEGIN
		update TBL_CATEGORY 
		set isdelete = 1 
		where id = @DATA1
		select * from TBL_CATEGORY where isdelete = 0
	END
	
	
	IF(@PROCESS='GETFILES')
	BEGIN
		select * from TBL_CATEGORY where isdelete = 0
	END


	IF(@PROCESS='GETCATEGORY')
	BEGIN
		select * from TBL_CATEGORY where isdelete = 0 AND CATEGORY=@DATA1
	END
END

