CREATE proc [dbo].[USP_SBperformance]        
@SBCODE varchar(30)= null,                     
@access_to varchar(30) =null,   --broker                    
@access_code varchar(30)=null, --SB        
@usertype varchar(20)=null,                    
@Mode varchar(20)           
as        
begin        
if(@Mode='Select' and @usertype ='ADMIN')        
begin        
      
      
        
select *,        
Revenue=CAST(0.00 as numeric(10,2)),         
RevenueTarget=cast(0.00 as numeric(10,2)),        
NewClientTarget=cast(0.00 as numeric(10,2)),        
Revenue_Perc=cast(0.00 as numeric(10,2)),        
NewClient_Perc=cast(0.00 as numeric(10,2))         
into #TABLE        
from fun_SBperformanceTest(@access_to,@access_code,@SBCODE)        
        
        
        
update #TABLE set RevenueTarget=(        
                                 select  top 1  MonthPotential  from INTRANET.RISK.DBO.tbl_Revenue_Potential         
                                 where  Year(CreatedOn)=YEAR(GETDATE()) and SBTAG=@SBCODE and  rtrim(ltrim(Potential_Month))=DATENAME(month, GETDATE())        
                                 ),         
                   
                  NewClientTarget=(        
                                    select top 1  MonthPotential   from INTRANET.RISK.DBO.tbl_NewClient_Potential         
                                   where  Year(CreatedOn)=YEAR(GETDATE())and SBTAG=@SBCODE and  rtrim(ltrim(Potential_Month))=DATENAME(month, GETDATE())        
                                   )             
                                  
              
update #TABLE set RevenueTarget =ISNULL(RevenueTarget ,0) ,NewClientTarget=ISNULL(NewClientTarget ,0)        
update #table set   Revenue= MonthlyRevenue/TradingDays where TradingDays >0        
update #table  set Revenue_Perc=(Revenue /RevenueTarget)*100 where RevenueTarget>0        
update #table  set  NewClient_Perc=(newclient /NewClientTarget)*100  where NewClientTarget>0        
                     
            
--select Revenue,RevenueTarget,Revenue_Perc,NewClient,NewClientTarget,NewClient_Perc  from #TABLE          
        


INSERT INTO TBL_REVENUDASHBOARD(SBCODE,Revenue,RevenueTarget,Revenue_Perc,NewClient,NewClientTarget,NewClient_Perc) 
select @SBCODE, Revenue=CONVERT(INT ,ROUND(Revenue,0)),RevenueTarget=CONVERT(INT,ROUND(RevenueTarget,0)),
Revenue_Perc=CONVERT(INT,ROUND(Revenue_Perc,0)),NewClient=CONVERT(INT,ROUND(NewClient,0)),NewClientTarget=CONVERT(INT,ROUND(NewClientTarget,0)),
NewClient_Perc=CONVERT(INT,ROUND(NewClient_Perc,0))  from #TABLE          
        
end        
        
end

