SELECT BMUSERID ,PURPOSE , VISITDATE  AS VISITDATE ,SBTAG ,Region FROM (SELECT  a.BMUSERID ,C.PURPOSE , A.VISITDATE  AS VISITDATE ,A.SBTAG ,D.Region,A.ID  FROM TBL_B2BMAINTRANSACTION A 
