CREATE PROC [dbo].[USP_UPLOADFINALTARGET]
@TYPE_TARGET  TYPE_TARGET READONLY,
@FLAG   VARCHAR(50)
AS BEGIN


IF @FLAG='TRUE'
BEGIN

		----CHECK EXISTING DATA
		SELECT * INTO #TYPE_TARGET FROM @TYPE_TARGET

		SELECT A.* INTO #EXISTANCE FROM #TYPE_TARGET A 
		JOIN TBL_B2B_TARGETS B
		ON A.BMID=B.BMID AND A.BMTYPE=B.BMTYPE

		DELETE A
		FROM #TYPE_TARGET A 
		JOIN TBL_B2B_TARGETS B
		ON A.BMID=B.BMID AND A.BMTYPE=B.BMTYPE

        INSERT INTO TBL_B2B_TARGETS(BMID,TITANIUM,PLATINUM,DIAMOND,GOLD,SILVER,NEW,BMTYPE)
		SELECT upper(BMID),TITANIUM,PLATINUM,DIAMOND,GOLD,SILVER,NEW,upper(BMTYPE) FROM #TYPE_TARGET


		select * from #TYPE_TARGET

		DECLARE @CNT AS INT =0

		SELECT @CNT=COUNT(1) FROM #TYPE_TARGET

		DECLARE @TYPE_TARGET1 AS TYPE_TARGET

		INSERT INTO @TYPE_TARGET1
		SELECT * FROM #TYPE_TARGET


		IF (@CNT>0)
		BEGIN
		EXEC USP_POSTFINALTARGET @FLAG,@TYPE_TARGET1 
		END
		
		--SELECT 0,BMID,'Record already exists' FROM #EXISTANCE

END
ELSE
BEGIN 

		TRUNCATE TABLE TBL_B2B_TARGETS

		INSERT INTO TBL_B2B_TARGETS(BMID,TITANIUM,PLATINUM,DIAMOND,GOLD,SILVER,NEW,BMTYPE)
		SELECT upper(BMID),TITANIUM,PLATINUM,DIAMOND,GOLD,SILVER,NEW,upper(BMTYPE) FROM @TYPE_TARGET


		select * from @TYPE_TARGET

		SELECT @CNT=COUNT(1) FROM @TYPE_TARGET
		IF (@CNT>0)
		BEGIN
		EXEC USP_POSTFINALTARGET @FLAG,@TYPE_TARGET
		END

		

END




END

