 CREATE PROC [dbo].[USP_UPLOADMAPPLING]
 (
 @PROCESS AS VARCHAR(50)='',
 @TYPE AS VARCHAR(50)='FULL' 
 )
 AS BEGIN


IF @PROCESS ='TRUNCATE'
BEGIN
    TRUNCATE TABLE tbl_b2bmapping_temp
END

IF @PROCESS ='UPLOAD'
BEGIN
CREATE TABLE #INVALIDDATA 
 (
  LineNum  int,
  bid  VARCHAR(50),
  SBcode  VARCHAR(50),
  Region  VARCHAR(50),
  Error  varchar(100)
 )
 CREATE TABLE #VALIDDATA 
 (
 
  bid  VARCHAR(50),
  SBcode  VARCHAR(50),
  Region  VARCHAR(50),
 )


INSERT INTO #INVALIDDATA (LineNum,bid,SBcode,Region,Error)
SELECT Id,BID,SBCode,Region,'INVALID DATA IN BMCODE OR SBCODE' FROM  tbl_b2bmapping_temp WHERE BID LIKE '%[^0-9a-zA-Z ]%' OR SBCode LIKE '%[^0-9a-zA-Z ]%'



INSERT INTO #INVALIDDATA (LineNum,bid,SBcode,Region,Error)
SELECT Id,BID,SBCode,Region,'BM ID should not be blank' FROM  tbl_b2bmapping_temp WHERE rtrim(ltrim(BID)) ='' 

 


INSERT INTO #INVALIDDATA (LineNum,bid,SBcode,Region,Error)
SELECT Id,BID,SBCode,Region,'SBCode should not be blank' FROM  tbl_b2bmapping_temp WHERE rtrim(ltrim(SBCode)) ='' 



INSERT INTO #INVALIDDATA (LineNum,bid,SBcode,Region,Error)
SELECT Id,BID,SBCode,Region,'Region should not be blank' FROM  tbl_b2bmapping_temp WHERE rtrim(ltrim(Region)) ='' 



DELETE FROM   tbl_b2bmapping_temp WHERE BID LIKE '%[^0-9a-zA-Z ]%'

DELETE FROM   tbl_b2bmapping_temp WHERE rtrim(ltrim(BID)) =''

DELETE FROM   tbl_b2bmapping_temp WHERE rtrim(ltrim(SBCode)) ='' 

DELETE FROM   tbl_b2bmapping_temp WHERE rtrim(ltrim(Region)) =''


delete from tbl_b2bmapping_temp where id in (
select min(id) as id from tbl_b2bmapping_temp
group by bid,sbcode
having count(1)>1)







IF @TYPE='FULL'
BEGIN


 
 
 
 INSERT INTO tbl_B2BMapping_trail
 SELECT BID,SBCode,IsDelete,Region,GETDATE() FROM tbl_B2BMapping
 
 TRUNCATE TABLE  tbl_B2BMapping
 
 INSERT INTO   tbl_B2BMapping
 SELECT upper( BID) ,upper(SBCode),0,upper(Region) FROM  tbl_b2bmapping_temp

 SELECT * FROM #INVALIDDATA order by LineNum

SELECT * FROM tbl_B2BMapping

DROP TABLE #INVALIDDATA

DROP TABLE  #VALIDDATA
 


END

IF @TYPE='INCREMENT'
BEGIN

DECLARE @bid varchar(50)
DECLARE @SBcode varchar(50)
declare @Region varchar(50)
DECLARE @ID VARCHAR(50)


DECLARE BMMapp CURSOR 
STATIC FOR   
SELECT ID, bid, SBcode ,Region
FROM tbl_b2bmapping_temp 


OPEN BMMapp   
FETCH NEXT FROM BMMapp INTO @ID, @bid, @SBcode ,@Region   

WHILE @@FETCH_STATUS = 0   
BEGIN   
       
       ---CHECK EXISTS
       DECLARE @CNT AS INT=0
       
       
       SELECT @CNT= SUM(CNT) FROM  (SELECT COUNT(1) CNT FROM  #VALIDDATA WHERE bid=@bid AND  SBcode=@SBcode
          UNION 
          SELECT COUNT(1) CNT FROM  tbl_b2bmapping WHERE bid=@bid AND  SBcode=@SBcode
       ) A
       
       IF @CNT >0
       BEGIN
          INSERT INTO #INVALIDDATA (LineNum,bid,SBcode,Region,Error)
          SELECT @ID, @bid, @SBcode ,@Region ,'DUPLICATE DATA IN FILE OR ALREADY EXIST IN DATABASE'
       END
       ELSE
       BEGIN
       
       INSERT INTO #VALIDDATA(bid,SBcode,Region)
       SELECT upper( @bid), upper(@SBcode) ,upper(@Region)
       END
       
       FETCH NEXT FROM BMMapp INTO @ID, @bid, @SBcode ,@Region  
END   

CLOSE BMMapp   
DEALLOCATE BMMapp


  DECLARE @C AS INT=0
 

 SELECT @C =COUNT(1) FROM #VALIDDATA 
 
 IF @C >0
 BEGIN
  
 INSERT INTO   tbl_B2BMapping
 SELECT BID ,SBCode,0,Region FROM  #VALIDDATA
 
 END

 SELECT * FROM #INVALIDDATA order by LineNum

SELECT * FROM #VALIDDATA

DROP TABLE #INVALIDDATA

DROP TABLE  #VALIDDATA

END



END





END

