		 DELETE FROM   #TYPE_SCHEDULE WHERE BMID LIKE '%[^0-9a-zA-Z ]%' OR SBCODE LIKE '%[^0-9a-zA-Z ]%' --OR ISDATE (PLANDATE)=0


		 ---CHECK BLANK RECORD--START

