		  SELECT ID,BMID,SBCODE,'INVALID BMID' FROM #TYPE_SCHEDULE
		  WHERE  BMID=''

		  DELETE FROM #TYPE_SCHEDULE WHERE BMID=''

		  INSERT INTO #INVALIDDATA (LINENUM,BID,SBCODE,ERROR)
		  SELECT ID,BMID,SBCODE,'INVALID SBCODE' FROM #TYPE_SCHEDULE
		  WHERE  SBCODE=''

		  INSERT INTO #INVALIDDATA (LINENUM,BID,SBCODE,ERROR)
		  SELECT ID,BMID,SBCODE,'INVALID SBNAME' FROM #TYPE_SCHEDULE
		  WHERE  SBNAME=''

		  DELETE FROM #TYPE_SCHEDULE WHERE SBNAME=''

		  INSERT INTO #INVALIDDATA (LINENUM,BID,SBCODE,ERROR)
		  SELECT ID,BMID,SBCODE,'INVALID SBADDRESS' FROM #TYPE_SCHEDULE
		  WHERE  SBADDRESS=''

		  DELETE FROM #TYPE_SCHEDULE WHERE SBADDRESS=''

