		  SELECT ID,BMID,SBCODE,'INVALID PLANDATE' FROM #TYPE_SCHEDULE
		  WHERE  PLANDATE=''

		 DELETE FROM #TYPE_SCHEDULE WHERE PLANDATE=''
		 ---CHECK BLANK RECORD--END

	

        INSERT INTO #INVALIDDATA (LINENUM,BID,SBCODE,ERROR)
