		WHERE  DBO.VALIDATEDATE(PLANDATE)=0

		DELETE  FROM #TYPE_SCHEDULE
		WHERE  DBO.VALIDATEDATE(PLANDATE)=0


		INSERT INTO #INVALIDDATA (LINENUM,BID,SBCODE,ERROR)
