		WHERE  MONTH( CONVERT(DATE, PLANDATE, 103))> MONTH(GETDATE()) OR YEAR( CONVERT(DATE, PLANDATE, 103)) > YEAR(GETDATE())

		DELETE FROM   #TYPE_SCHEDULE   WHERE  MONTH( CONVERT(DATE, PLANDATE, 103))> MONTH(GETDATE()) OR  YEAR( CONVERT(DATE, PLANDATE, 103)) > YEAR(GETDATE())





		INSERT INTO #INVALIDDATA (LINENUM,BID,SBCODE,ERROR)
		SELECT A.ID,A.BMID,A.SBCODE ,'SB NOT MAPPED' FROM #TYPE_SCHEDULE A
		LEFT JOIN TBL_B2BMAPPING B
		ON  A.BMID=B.BID AND A.SBCODE=B.SBCODE
		WHERE B.ID IS NULL

		DELETE A
		FROM #TYPE_SCHEDULE A
		LEFT JOIN TBL_B2BMAPPING B
		ON  A.BMID=B.BID AND A.SBCODE=B.SBCODE
		WHERE B.ID IS NULL



		IF @FLAG='TRUE'
		BEGIN

			INSERT INTO TBL_MEETINGSCHEDULE(BMID,SBCODE,SBNAME,SBADDRESS,PLANDATE)

