		ELSE 
		BEGIN
			
			DELETE TBL_MEETINGSCHEDULE
			WHERE MONTH( CONVERT(DATE, PLANDATE, 103))= MONTH(GETDATE()) and  YEAR( CONVERT(DATE, PLANDATE, 103)) = YEAR(GETDATE())

			INSERT INTO TBL_MEETINGSCHEDULE(BMID,SBCODE,SBNAME,SBADDRESS,PLANDATE)

			SELECT UPPER( BMID),UPPER(SBCODE),UPPER(SBNAME),UPPER(SBADDRESS),PLANDATE FROM  #TYPE_SCHEDULE
		END 

		SELECT * FROM #TYPE_SCHEDULE
        SELECT * FROM #INVALIDDATA ORDER BY LINENUM

