CREATE proc acc_BankSlipReprint      
@cltcode  varchar(10),      
@booktype varchar(2),      
@startslip int,      
@endslip int
As      
      
/*      
select  SlipNo, ddno, slipdate, acname, bnkname, brnname, relamt, cltcode, l1.vno, l1.lno      
