 AND L.VDT > = @SDTCUR + ' 00:00:00'           
 AND L.VDT <= @LDTCUR + ' 23:59:59'           
 AND           
 (          
  A.ACTYP LIKE 'ASS%'           
  OR A.ACTYP LIKE 'LIAB%'          
 )           
 AND L2.CLTCODE NOT IN (@@MAINCTRLAC, @PNLCODE)           
GROUP BY COSTCODE,           
 L2.CLTCODE,           
 A.ACNAME    
*/    
INSERT           
  INTO BRANCHWISECLBAL           
 SELECT          
   @@MAINCTRLAC,           
   @@MAINACNAME,           
   COSTCODE,          
   -(SUM(BALANCE))    
  FROM          
   BRANCHWISECLBAL          
  GROUP BY          
   COSTCODE     
    
        
    
