  FETCH NEXT FROM          
   @@RCURSOR           
  INTO          
   @@COSTCODE,          
   @@AMT          
 END          
          
CLOSE @@RCURSOR          
DEALLOCATE @@RCURSOR         
*/     
          
INSERT           
INTO LEDGER2           
SELECT           
 L.VTYP,           
 L.VNO,           
 L.LNO,           
 (          
 CASE           
  WHEN (CASE WHEN COSTCODE = @@MAINCOSTCODE AND L.CLTCODE = @@MAINCTRLAC THEN 0 ELSE BALANCE END) >= 0           
  THEN 'D'           
  ELSE 'C'           
 END          
 ),           
 ABS(CASE WHEN COSTCODE = @@MAINCOSTCODE AND L.CLTCODE = @@MAINCTRLAC THEN 0 ELSE BALANCE END),           
