-- --------------------------------------------------
-- PROCEDURE dbo.CBO_ADDEDITPARAMETER
-- --------------------------------------------------


--EXEC CBO_ADDEDITPARAMETER '13/12/2008','26/12/2009',0,0,0,0,0,0,0,21,12,'D','BROKER','BROKER'    
    
CREATE PROCEDURE CBO_ADDEDITPARAMETER  
    
 @SDTCUR VARCHAR(11),    
 @LDTCUR VARCHAR(11),    
 @CURYEAR TINYINT,    
 @VNOFLAG SMALLINT,    
 @MATCH_BTOB SMALLINT,    
 @MATCH_CTOC SMALLINT,    
 @MAKER_CHECKER SMALLINT,    
 @VOUCHERPRINTFLAG SMALLINT,    
 @BRANCHFLAG SMALLINT,    
 @FLDAUTO INT,    
 @REPORTDAYS INT,    
 @FLAG VARCHAR(1),     
 @STATUSID VARCHAR(25) = 'BROKER',    
 @STATUSNAME VARCHAR(25) = 'BROKER'    
AS    
 DECLARE    
 @LDTPRV DATETIME,    
 @SDTNXT DATETIME    
    
 IF @STATUSID <> 'BROKER'     
  BEGIN    
   RAISERROR ('This Procedure is accessible to Broker', 16, 1)    
   RETURN    
  END    
 IF @FLAG <> 'A' AND @FLAG <> 'E' AND @FLAG <> 'D'    
  BEGIN    
   RAISERROR ('Add/Edit Flags Not Set Properly', 16, 1)    
   RETURN    
  END    
 IF LEN(@SDTCUR) = 10 AND CHARINDEX('/', @SDTCUR) > 0    
  BEGIN    
   SET @SDTCUR = CONVERT(VARCHAR(11), CONVERT(DATETIME, @SDTCUR, 103), 109)    
  END    
 IF LEN(@LDTCUR) = 10 AND CHARINDEX('/', @LDTCUR) > 0    
  BEGIN    
   SET @LDTCUR = CONVERT(VARCHAR(11), CONVERT(DATETIME, @LDTCUR, 103), 109)    
  END    
 SET @LDTPRV = CONVERT(DATETIME, @SDTCUR, 109) - '00:00:01.000'    
 SET @SDTNXT = CONVERT(DATETIME, @LDTCUR, 109) + 1    
 IF @FLAG = 'A'    
  BEGIN    
   IF (SELECT COUNT(1) FROM PARAMETER WHERE (@SDTCUR BETWEEN SDTCUR AND LDTCUR) OR (@LDTCUR BETWEEN SDTCUR AND LDTCUR)) > 0    
    BEGIN    
     RAISERROR ('Financial Years cannot be overlapped...', 16, 1)    
     RETURN    
    END    
   IF @CURYEAR = 1    
    BEGIN    
     UPDATE    
      PARAMETER    
     SET    
      CURYEAR = 0    
    END    
   INSERT INTO PARAMETER    
    (    
     SDTCUR,    
     LDTCUR,    
     LDTPRV,    
     SDTNXT,    
     CURYEAR,    
     VNOFLAG,    
     MATCH_BTOB,    
     MATCH_CTOC,    
     MAKER_CHECKER,    
     VOUCHERPRINTFLAG,    
     BRANCHFLAG,    
     REPORTDAYS    
    )    
   VALUES    
    (    
     @SDTCUR,    
     @LDTCUR,    
     @LDTPRV,    
     @SDTNXT,    
     @CURYEAR,    
     @VNOFLAG,    
     @MATCH_BTOB,    
     @MATCH_CTOC,    
     @MAKER_CHECKER,    
     @VOUCHERPRINTFLAG,    
     @BRANCHFLAG,    
     @REPORTDAYS    
    )    
 END    
 ELSE IF @FLAG = 'E'    
  BEGIN    
   IF @CURYEAR = 1    
    BEGIN    
     UPDATE    
      PARAMETER    
     SET    
      CURYEAR = 0    
    END    
   UPDATE    
    PARAMETER    
   SET    
    MATCH_BTOB = @MATCH_BTOB,    
    MATCH_CTOC = @MATCH_CTOC,    
    MAKER_CHECKER = @MAKER_CHECKER,    
    CURYEAR = @CURYEAR,    
    VOUCHERPRINTFLAG = @VOUCHERPRINTFLAG,    
    BRANCHFLAG = @BRANCHFLAG,    
    REPORTDAYS = @REPORTDAYS    
   WHERE    
    FLDAUTO = @FLDAUTO    
  END    
 ELSE IF @FLAG = 'D'    
  BEGIN    
   SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED    
   IF EXISTS (SELECT TOP 1 * FROM LEDGER WHERE VDT BETWEEN @SDTCUR AND @LDTCUR + ' 23:59:59')    
    BEGIN    
     RAISERROR ('Financial Year cannot be deleted as some of the entries are present in Ledger...', 16, 1)    
     RETURN    
    END    
   DELETE FROM PARAMETER WHERE FLDAUTO = @FLDAUTO    
  END

GO

