CREATE  PROCEDURE CBO_GETMULTIBANK  
@ShareServer VARCHAR(100),
@ShareDb  VARCHAR(100),
@AccountServer VARCHAR(100),
@AccountDb  VARCHAR(100),
@PARTYCODE varchar(50),  
@STATUSID VARCHAR(25) = 'BROKER',  
@STATUSNAME VARCHAR(25) = 'BROKER'   
AS  
BEGIN  
  
 DECLARE @SQL VARCHAR(1000)  
 SET @SQL='SELECT upper(MB.Cltcode)as Cltcode, upper(isnull(MB.BankID,'''')) as BankId, upper(PO.Bank_Name) as Bank_Name,  upper(branch_name)as branch_name,  upper(AccNo)as AccNo,  
