/*
TRUNCATE TABLE A
CCOUNT..TEMP_FINAL
	EXEC TEMP_DPC_CALC 'MAY  1 2014','DEC 31 2014','A0','AZZ','NSE','FUTURES'
	EXEC ACCOUNT..CHK_GET_DPC_CLIENTS '0A141', '0A149', 'APR  1 2014', 'NSE', 'CAPITAL'    
