	,@LEDGER_FDATE VARCHAR(11)
	
	
SELECT @LEDGER_FDATE = CONVERT(VARCHAR(11), SDTCUR, 109) FROM PARAMETER WHERE @TDATE BETWEEN SDTCUR AND LDTCUR
	
	
