	SELECT VTYPE=.DBO.PIECE(ITEMS,'|',1)
	,BOOKTYPE=.DBO.PIECE(ITEMS,'|',2)
	,TYPE=.DBO.PIECE(ITEMS,'|',3)
	,DRCR=.DBO.PIECE(ITEMS,'|',4)
	,EXCLUSION_FLAG =.DBO.PIECE(ITEMS,'|',5)
