		,DPC_DATE = CONVERT(VARCHAR(11),(CASE WHEN [TYPE]= 'V' THEN L.VDT ELSE L.EDT END),109)
		,LEDGER_BALANCE = CASE  WHEN L.DRCR = 'D' THEN -VAMT ELSE VAMT END
		,VTYP
		,L.BOOKTYPE
		,[TYPE] = ISNULL([TYPE],'V')
		,EXCLUSION_FLAG= ISNULL(EXCLUSION_FLAG,'0')
		,EXMPT_DAYS = 0
		,CALENDER_DAYS = 0
		,SETTLEMENT_DATE = CONVERT(VARCHAR(11),(CASE WHEN [TYPE]= 'V' THEN L.VDT ELSE L.EDT END),109)
