SELECT ID = (ROW_NUMBER() OVER(ORDER BY START_DATE ASC)), SETT_DATE=START_DATE INTO #TEMP_SETTMST_MARGINCOLL  FROM MSAJAG..SETT_MST WHERE SETT_TYPE = 'N' AND START_DATE>=@FR_CALENDER_DATE AND START_DATE <=@TDATE+' 23:59' ORDER BY START_DATE ASC 

SELECT START_DATE = DATE ,SETT_DATE INTO #MARGINCOLL_CALENDAR FROM  (SELECT * FROM #MAPDATE) B LEFT OUTER JOIN #TEMP_SETTMST_MARGINCOLL A ON A.SETT_DATE = B.DATE
 
 UPDATE A /** FILLING MISSING DATES REQUIRED AS PER THE MARGIN AND COLLATERAL LOGIC **/
 SET  
	A.SETT_DATE = (SELECT MAX(SETT_DATE) FROM #MARGINCOLL_CALENDAR B WHERE B.START_DATE <= A.START_DATE AND SETT_DATE IS NOT NULL)
 FROM #MARGINCOLL_CALENDAR A 
