-- --------------------------------------------------
CREATE PROCEDURE [DBO].[CLS_GET_ACC_LEDGER_FORMULA_BALANCES] 
	@FR_DATE VARCHAR(11),-- FROM DATE OF THE PERIOD 
	@EFFDATE VARCHAR(11),-- DATE FOR WHICH THE BALANCE IS REQUIRED  
	@PROCESS_CODE VARCHAR(10),-- PROCESS CODE FROM THE FORMULA MASTER  
	@REPORT_FORMULA VARCHAR(MAX),-- IN CASE NOT TO SET THE FORMULA AND GET THE SPECIFIEC FIELDS
	@EXCHANGE VARCHAR(10) = '',-- EXCHANGE
	@SEGMENT VARCHAR(50) = '',-- SEGMENT
	@REPORT_COLUMN VARCHAR(1000) = '', 
	@REPORT_GROUPING VARCHAR(1000) = '',
	@TABLE_NAME  VARCHAR(1000) = '' 
	
AS
SELECT @REPORT_FORMULA = FORMULANAME
FROM FORMULA_MASTER
WHERE PROCESSCODE = @PROCESS_CODE

IF @EXCHANGE = ''
BEGIN
	SELECT @EXCHANGE = EXCHANGE
		,@SEGMENT = SEGMENT
	FROM OWNER
END

DECLARE @START_DATE VARCHAR(11)

IF LEN(@FR_DATE) = 10
	SELECT @FR_DATE = CONVERT(VARCHAR(11), CONVERT(DATETIME, @FR_DATE, 103), 109)

IF LEN(@EFFDATE) = 10
BEGIN
	SELECT @EFFDATE = CONVERT(VARCHAR(11), CONVERT(DATETIME, @EFFDATE, 103), 109)
END

SELECT @START_DATE = CONVERT(VARCHAR(11), SDTCUR, 109)
FROM PARAMETER
WHERE @EFFDATE BETWEEN SDTCUR
		AND LDTCUR

DECLARE @MIN_BILL_DATE VARCHAR(11)
	,@MAX_BILL_DATE VARCHAR(11)

SELECT @MIN_BILL_DATE = CONVERT(VARCHAR(11), MIN(VDT), 109)
	,@MAX_BILL_DATE = CONVERT(VARCHAR(11), MAX(VDT), 109)
FROM (
	SELECT TOP 2 *
	FROM BILLPOSTED
	WHERE VDT <= @EFFDATE
	ORDER BY VDT DESC
	) A

CREATE TABLE #REPORT_DATA (
	CLTCODE VARCHAR(10)
	,VDTBAL_OPBAL MONEY
	,EDTBAL_OPBAL MONEY
	,VDTBAL_DATES MONEY
	,EDTBAL_DATES MONEY
	,VDTBAL_DATES_CREDIT MONEY
	,VDTBAL_DATES_DEBIT MONEY
	,EDTBAL_DATES_CREDIT MONEY
	,EDTBAL_DATES_DEBIT MONEY
	,VDTBAL MONEY
	,EDTBAL MONEY
	,VDTBAL_RECEIPT MONEY
	,T_DAY_BILL MONEY
	,T1_DAY_BILL MONEY
	,VDTBAL_BILL_DR MONEY
	,VDTBAL_BILL_CR MONEY
	,VDTBAL_NONBILL MONEY
	,EDTBAL_RECEIPT MONEY
	,EDTBAL_BILL_DR MONEY
	,EDTBAL_BILL_CR MONEY
	,EDTBAL_NONBILL MONEY
	,FDT_RECEIPT MONEY
	,FDT_CREDIT_BILLS MONEY
	,FDT_DEBIT_BILLS MONEY
	,FDT_CR MONEY
	,FDT_DR MONEY
	,MARGIN_BAL MONEY
	)

DECLARE @@SQL VARCHAR(MAX)
INSERT INTO #REPORT_DATA
SELECT CLTCODE
	,VDTBAL_OPBAL = SUM(VDTBAL_OPBAL)
	,EDTBAL_OPBAL = SUM(EDTBAL_OPBAL)
	,VDTBAL_DATES = SUM(VDTBAL_DATES)
	,EDTBAL_DATES = SUM(EDTBAL_DATES)
	,VDTBAL_DATES_CREDIT = SUM(VDTBAL_DATES_CREDIT)
	,VDTBAL_DATES_DEBIT = SUM(VDTBAL_DATES_DEBIT)
	,EDTBAL_DATES_CREDIT = SUM(EDTBAL_DATES_CREDIT)
	,EDTBAL_DATES_DEBIT = SUM(VDTBAL_DATES_DEBIT)
	,VDTBAL = SUM(VDTBAL)
	,EDTBAL = SUM(EDTBAL)
	,VDTBAL_RECEIPT = SUM(VDTBAL_RECEIPT)
	,T_DAY_BILL = SUM(T_DAY_BILL)
	,T1_DAY_BILL = SUM(T1_DAY_BILL)
	,VDTBAL_BILL_DR = SUM(VDTBAL_BILL_DR)
	,VDTBAL_BILL_CR = SUM(VDTBAL_BILL_CR)
	,VDTBAL_NONBILL = SUM(VDTBAL_NONBILL)
	,EDTBAL_RECEIPT = SUM(EDTBAL_RECEIPT)
	,EDTBAL_BILL_DR = SUM(EDTBAL_BILL_DR)
	,EDTBAL_BILL_CR = SUM(EDTBAL_BILL_CR)
	,EDTBAL_NONBILL = SUM(EDTBAL_NONBILL)
	,FDT_RECEIPT = SUM(FDT_RECEIPT)
	,FDT_CREDIT_BILLS = SUM(FDT_CREDIT_BILLS)
	,FDT_DEBIT_BILLS = SUM(FDT_DEBIT_BILLS)
	,FDT_CR = SUM(FDT_CR)
	,FDT_DR = SUM(FDT_DR)
	,MARGIN_BAL = 0
FROM (
	SELECT CLTCODE = L.CLTCODE
		,VDTBAL_OPBAL = CASE 
			WHEN VDT >= @START_DATE
				AND VDT < @FR_DATE
				THEN SUM(CASE L.DRCR
							WHEN 'D'
								THEN VAMT
							ELSE - VAMT
							END)
			ELSE 0
			END
		,EDTBAL_OPBAL = CASE 
			WHEN EDT >= @START_DATE
				AND EDT < @FR_DATE
				THEN SUM(CASE L.DRCR
							WHEN 'D'
								THEN VAMT
							ELSE - VAMT
							END)
			ELSE 0
			END + CASE 
			WHEN EDT >= @START_DATE
				AND VDT < @START_DATE
				THEN SUM(CASE L.DRCR
							WHEN 'C'
								THEN VAMT
							ELSE - VAMT
							END)
			ELSE 0
			END
		,VDTBAL_DATES = CASE 
			WHEN VDT >= @FR_DATE
				THEN SUM(CASE L.DRCR
							WHEN 'D'
								THEN VAMT
							ELSE - VAMT
							END)
			ELSE 0
			END
		,EDTBAL_DATES = CASE 
			WHEN EDT >= @FR_DATE
				AND EDT <= @EFFDATE + ' 23:59'
				THEN SUM(CASE L.DRCR
							WHEN 'D'
								THEN VAMT
							ELSE - VAMT
							END)
			ELSE 0
			END
		,VDTBAL_DATES_CREDIT = CASE 
			WHEN VDT >= @FR_DATE
				THEN SUM(CASE L.DRCR
							WHEN 'C'
								THEN VAMT
							ELSE 0
							END)
			ELSE 0
			END
		,VDTBAL_DATES_DEBIT = CASE 
			WHEN VDT >= @FR_DATE
				THEN SUM(CASE L.DRCR
							WHEN 'D'
								THEN VAMT
							ELSE 0
							END)
			ELSE 0
			END
		,EDTBAL_DATES_CREDIT = CASE 
			WHEN EDT >= @FR_DATE
				AND EDT <= @EFFDATE + ' 23:59'
				THEN SUM(CASE L.DRCR
							WHEN 'C'
								THEN VAMT
							ELSE 0
							END)
			ELSE 0
			END
		,EDTBAL_DATES_DEBIT = CASE 
			WHEN EDT >= @FR_DATE
				AND EDT <= @EFFDATE + ' 23:59'
				THEN SUM(CASE L.DRCR
							WHEN 'D'
								THEN VAMT
							ELSE 0
							END)
			ELSE 0
			END
		,VDTBAL = CASE 
			WHEN VDT >= @START_DATE
				THEN SUM(CASE L.DRCR
							WHEN 'D'
								THEN VAMT
							ELSE - VAMT
							END)
			ELSE 0
			END
		,EDTBAL = CASE 
			WHEN EDT >= @START_DATE
				AND EDT <= @EFFDATE + ' 23:59'
				THEN SUM(CASE L.DRCR
							WHEN 'D'
								THEN VAMT
							ELSE - VAMT
							END)
			ELSE 0
			END + CASE 
			WHEN EDT >= @START_DATE
				AND VDT < @START_DATE
				THEN SUM(CASE L.DRCR
							WHEN 'C'
								THEN VAMT
							ELSE - VAMT
							END)
			ELSE 0
			END
		,VDTBAL_RECEIPT = CASE 
			WHEN VDT >= @START_DATE
				AND VTYP = 2
				THEN SUM(VAMT)
			ELSE 0
			END
		,T_DAY_BILL = CASE 
			WHEN VDT LIKE @MAX_BILL_DATE + '%'
				AND VTYP = 15
				THEN SUM(CASE 
							WHEN L.DRCR = 'D'
								THEN VAMT
							ELSE - VAMT
							END)
			ELSE 0
			END
		,T1_DAY_BILL = CASE 
			WHEN VDT LIKE @MIN_BILL_DATE + '%'
				AND VTYP = 15
				THEN SUM(CASE 
							WHEN L.DRCR = 'D'
								THEN VAMT
							ELSE - VAMT
							END)
			ELSE 0
			END
		,VDTBAL_BILL_DR = CASE 
			WHEN VDT >= @START_DATE
				AND VTYP = 15
				AND L.DRCR = 'D'
				THEN SUM(VAMT)
			ELSE 0
			END
		,VDTBAL_BILL_CR = CASE 
			WHEN VDT >= @START_DATE
				AND VTYP = 15
				AND L.DRCR = 'C'
				THEN SUM(VAMT)
			ELSE 0
			END
		,VDTBAL_NONBILL = CASE 
			WHEN VDT >= @START_DATE
				AND VTYP NOT IN (
					15
					,2
					)
				THEN SUM(CASE L.DRCR
							WHEN 'D'
								THEN VAMT
							ELSE - VAMT
							END)
			ELSE 0
			END
		,EDTBAL_RECEIPT = CASE 
			WHEN EDT >= @START_DATE
				AND EDT <= @EFFDATE + ' 23:59'
				AND VTYP = 2
				THEN SUM(VAMT)
			ELSE 0
			END + CASE 
			WHEN EDT >= @START_DATE
				AND VDT < @START_DATE
				AND VTYP = 2
				THEN SUM(- VAMT)
			ELSE 0
			END
		,EDTBAL_BILL_DR = CASE 
			WHEN EDT >= @START_DATE
				AND EDT <= @EFFDATE + ' 23:59'
				AND VTYP = 15
				AND L.DRCR = 'D'
				THEN SUM(VAMT)
			ELSE 0
			END + CASE 
			WHEN EDT >= @START_DATE
				AND VDT < @START_DATE
				AND VTYP = 15
				AND L.DRCR = 'D'
				THEN SUM(- VAMT)
			ELSE 0
			END
		,EDTBAL_BILL_CR = CASE 
			WHEN EDT >= @START_DATE
				AND EDT <= @EFFDATE + ' 23:59'
				AND VTYP = 15
				AND L.DRCR = 'C'
				THEN SUM(VAMT)
			ELSE 0
			END + CASE 
			WHEN EDT >= @START_DATE
				AND VDT < @START_DATE
				AND VTYP = 15
				AND L.DRCR = 'C'
				THEN SUM(- VAMT)
			ELSE 0
			END
		,EDTBAL_NONBILL = CASE 
			WHEN EDT >= @START_DATE
				AND EDT <= @EFFDATE + ' 23:59'
				AND VTYP NOT IN (
					15
					,2
					)
				THEN SUM(CASE L.DRCR
							WHEN 'D'
								THEN VAMT
							ELSE - VAMT
							END)
			ELSE 0
			END + CASE 
			WHEN EDT >= @START_DATE
				AND VDT < @START_DATE
				THEN SUM(CASE L.DRCR
							WHEN 'C'
								THEN VAMT
							ELSE - VAMT
							END)
			ELSE 0
			END
		,FDT_RECEIPT = CASE 
			WHEN EDT > @EFFDATE + ' 23:59'
				AND VDT <= @EFFDATE + ' 23:59'
				AND VTYP = 2
				THEN SUM(VAMT)
			ELSE 0
			END
		,FDT_CREDIT_BILLS = CASE 
			WHEN EDT > @EFFDATE + ' 23:59'
				AND VDT <= @EFFDATE + ' 23:59'
				AND VTYP = 15
				AND DRCR = 'C'
				THEN SUM(VAMT)
			ELSE 0
			END
		,FDT_DEBIT_BILLS = CASE 
			WHEN EDT > @EFFDATE + ' 23:59'
				AND VDT <= @EFFDATE + ' 23:59'
				AND VTYP = 15
				AND DRCR = 'D'
				THEN SUM(VAMT)
			ELSE 0
			END
		,FDT_CR = CASE 
			WHEN EDT > @EFFDATE + ' 23:59'
				AND VDT <= @EFFDATE + ' 23:59'
				AND L.DRCR = 'C'
				AND VTYP <> 2
				THEN SUM(VAMT)
			ELSE 0
			END
		,FDT_DR = CASE 
			WHEN EDT > @EFFDATE + ' 23:59'
				AND VDT <= @EFFDATE + ' 23:59'
				AND L.DRCR = 'D'
				AND VTYP <> 2
				THEN SUM(VAMT)
			ELSE 0
			END
	FROM LEDGER L(NOLOCK)
		,#FORMULA_CLIENT_MASTER A
	WHERE A.CLTCODE = L.CLTCODE --AND SESSION_ID = @SESSIONID
		AND (
			VDT >= @START_DATE
			OR EDT >= @START_DATE
			)
		AND VDT <= @EFFDATE + ' 23:59'
	GROUP BY L.CLTCODE
		,VDT
		,EDT
		,VTYP
		,L.DRCR
	) A
GROUP BY CLTCODE

--HAVING SUM(VDTBAL) <> 0 AND SUM(EDTBAL) <> 0
DECLARE @ML_BAL_REQ TINYINT

SELECT @ML_BAL_REQ = CHARINDEX('MARGIN_BAL', @REPORT_FORMULA)

IF @ML_BAL_REQ > 0
BEGIN
	UPDATE RD
	SET MARGIN_BAL = ML.AMOUNT
	FROM #REPORT_DATA RD
		,(
			SELECT PARTY_CODE
				,AMOUNT = SUM(CASE DRCR
						WHEN 'C'
							THEN AMOUNT
						ELSE - AMOUNT
						END)
			FROM MARGINLEDGER ML
			WHERE VDT >= @START_DATE
				AND VDT <= @EFFDATE + ' 23:59'
			GROUP BY PARTY_CODE
			) ML
	WHERE RD.CLTCODE = PARTY_CODE
END

