    ORDER BY L.CLTCODE,VOUDT                              
   END                              
  ELSE                              
   BEGIN                              
    SELECT L.* FROM #TMPMLEDGER L,ACMAST A     
    WHERE  L.CLTCODE = A.CLTCODE AND ACCAT IN ('4','104')   
