     SELECT L.* FROM #TMPMLEDGER L,ACMAST A     
 WHERE L.CLTCODE = A.CLTCODE AND ACCAT IN ('4','104')   
 AND (CRAMT <> 0 OR DRAMT <> 0 OR OPBAL <> 0)  
 ORDER BY L.ACNAME,VOUDT                              
    END                              
   ELSE                              
    BEGIN                              
     SELECT L.* FROM #TMPMLEDGER L,ACMAST A    
 WHERE L.CLTCODE = A.CLTCODE AND ACCAT IN ('4','104')   
 AND (CRAMT <> 0 OR DRAMT <> 0 OR OPBAL <> 0)  
 ORDER BY L.ACNAME,EFFDT                              
    END                              
  END                              
                              
