 @FROM_PARTY VARCHAR(10),      
 @TO_PARTY VARCHAR(10)      
)      
AS      
      
CREATE TABLE #DPC_CHARGES      
(      
 DPC_DATE DATETIME,      
 CLTCODE VARCHAR(10),      
 LONG_NAME VARCHAR(100),      
 BRANCH_CD VARCHAR(10),      
 SUB_BROKER VARCHAR(25),      
 TRADER VARCHAR(25),      
 REGION VARCHAR(20),      
 AREA VARCHAR(20),      
 CL_TYPE VARCHAR(10),      
 DPC_BALANCE MONEY,      
 LEDGER_BALANCE MONEY,      
 CASH_COLLATREAL MONEY,      
 NONCASH_COLLATREAL MONEY,      
 MARGIN_REQ MONEY,      
 AMOUNT_REV MONEY,      
 DPC_PERCENTE MONEY      
)      
      
/*      
CREATE TABLE #DPC_CHARGES_FINAL      
(      
 DPC_DATE DATETIME,      
 CLTCODE VARCHAR(10),      
 LONG_NAME VARCHAR(100),      
 BRANCH_CD VARCHAR(10),      
