 TRADER VARCHAR(25),      
 REGION VARCHAR(20),      
 AREA VARCHAR(20),      
 CL_TYPE VARCHAR(10),      
 DPC_BALANCE MONEY,      
 LEDGER_BALANCE MONEY,      
 CASH_COLLATREAL MONEY,      
 NONCASH_COLLATREAL MONEY,      
 MARGIN_REQ MONEY,      
 DPC_CHARGE MONEY,      
 DPC_PERCENTE MONEY,      
 AMOUNT_REV MONEY      
)      
*/      
DECLARE      
 @@OPEN_DATE VARCHAR(11)      
       
SELECT       
 @@OPEN_DATE = CONVERT(VARCHAR(11), SDTCUR, 109)      
FROM       
 PARAMETER      
WHERE      
 @FROM_DATE BETWEEN SDTCUR AND LDTCUR      
      
/* TABLE TO MAINTAIN THE T + 2 AND T + 4 LBALANCES TO APPLY THE T + 4 DEBIT BALANCE LOGIC */      
CREATE TABLE #DEBIT_DEPC_BILLS      
(      
 CLTCODE VARCHAR(10),      
 T_2_DATE DATETIME,      
 T_4_DATE DATETIME,      
 BILL_AMOUNT MONEY,      
 VDT DATETIME      
)      
      
--, BRANCH_CD, SUB_BROKER, TRADER, REGION, AREA, CL_TYPE,       
      
      
INSERT INTO #DPC_CHARGES(DPC_DATE, CLTCODE, LONG_NAME, DPC_BALANCE, LEDGER_BALANCE, CASH_COLLATREAL, NONCASH_COLLATREAL, MARGIN_REQ, DPC_PERCENTE, BRANCH_CD, SUB_BROKER, TRADER, REGION, AREA, CL_TYPE, AMOUNT_REV)      
SELECT       
 BALDATE,       
 CLTCODE,       
 LONG_NAME,      
 SUM(DPC_BALANCE),       
 LEDGER_BALANCE = SUM(DPC_BALANCE),      
 SUM(CASH_COLLATREAL),       
 SUM(NONCASH_COLLATREAL),      
 SUM(MARGIN_REQ),      
 MAX(DPC_PERCENTE),      
 BRANCH_CD,       
 SUB_BROKER,       
 TRADER,       
 REGION,       
 AREA,       
 CL_TYPE,      
 AMOUNT_REV = 0      
FROM      
(      
 SELECT       
  BALDATE = @FROM_DATE,      
  CLTCODE,      
  LONG_NAME,      
  DPC_BALANCE = SUM(CASE DRCR WHEN 'D' THEN CASE WHEN VTYP <> 15 THEN VAMT ELSE 0 END ELSE -VAMT END),      
  CASH_COLLATREAL = 0,      
  NONCASH_COLLATREAL = 0,      
  MARGIN_REQ = 0,      
  DPC_PERCENTE = MAX(DEBIT_INT),      
  BRANCH_CD,       
  SUB_BROKER,       
  TRADER,       
  REGION,       
  AREA,       
  CL_TYPE      
 FROM      
  LEDGER L, .DBO.GET_DPC_CLIENTS(@FROM_PARTY, @TO_PARTY, @FROM_DATE) C      
 WHERE      
  L.CLTCODE = PARTY_CODE      
  AND VDT >= @@OPEN_DATE      
  AND VDT < @FROM_DATE      
 GROUP BY      
  CONVERT(DATETIME, CONVERT(VARCHAR(11), VDT, 109)),      
  CLTCODE,      
  LONG_NAME,      
  BRANCH_CD,       
  SUB_BROKER,       
  TRADER,       
  REGION,       
  AREA,       
  CL_TYPE      
 HAVING      
  SUM(CASE DRCR WHEN 'D' THEN CASE WHEN VTYP <> 15 THEN VAMT ELSE 0 END ELSE -VAMT END) <> 0      
        
 UNION ALL      
      
 SELECT       
  BALDATE = @FROM_DATE,      
  CLTCODE,      
  LONG_NAME,      
  DPC_BALANCE = SUM(VAMT),      
  CASH_COLLATREAL = 0,      
  NONCASH_COLLATREAL = 0,      
  MARGIN_REQ = 0,      
  DPC_PERCENTE = MAX(DEBIT_INT),      
  BRANCH_CD,       
  SUB_BROKER,       
  TRADER,       
  REGION,       
  AREA,       
  CL_TYPE      
 FROM      
  LEDGER L, .DBO.GET_DPC_CLIENTS(@FROM_PARTY, @TO_PARTY, @FROM_DATE) C      
 WHERE      
  L.CLTCODE = PARTY_CODE      
  AND EDT >= @@OPEN_DATE      
  AND EDT < @FROM_DATE      
  AND VTYP = 15 AND DRCR = 'D'      
 GROUP BY      
  CONVERT(DATETIME, CONVERT(VARCHAR(11), VDT, 109)),      
  CLTCODE,      
  LONG_NAME,      
  BRANCH_CD,       
  SUB_BROKER,       
  TRADER,       
  REGION,       
  AREA,       
  CL_TYPE      
 HAVING      
  SUM(CASE DRCR WHEN 'D' THEN CASE WHEN VTYP <> 15 THEN VAMT ELSE 0 END ELSE -VAMT END) <> 0      
        
 UNION ALL      
       
 SELECT       
  BALDATE = @FROM_DATE,      
  CLTCODE,      
  LONG_NAME,      
  DPC_BALANCE = SUM(-VAMT),      
  CASH_COLLATREAL = 0,      
  NONCASH_COLLATREAL = 0,      
  MARGIN_REQ = 0,      
  DPC_PERCENTE = MAX(DEBIT_INT),      
  BRANCH_CD,       
  SUB_BROKER,       
  TRADER,       
  REGION,       
  AREA,       
  CL_TYPE      
 FROM      
  LEDGER L, .DBO.GET_DPC_CLIENTS(@FROM_PARTY, @TO_PARTY, @FROM_DATE) C      
