-- PROCEDURE dbo.EDITRECONCILE
-- --------------------------------------------------


CREATE PROCEDURE EDITRECONCILE      
                @code    VARCHAR(10),      
                @frmdate VARCHAR(11),      
                @gdate   VARCHAR(11),      
                @gdrcr   VARCHAR(1),      
                @flag    VARCHAR(1),      
                @tVamt   NUMERIC(17,2)      
AS      
  IF @flag = 1      
    BEGIN      
      SET TRANSACTION ISOLATION  LEVEL  READ  UNCOMMITTED      
            
      SELECT   ISNULL(SUM(VAMT),0),      
               DRCR      
      FROM     LEDGER WITH (nolock)      
      WHERE    CLTCODE = @code      
               AND VDT <= @gdate      
      GROUP BY DRCR      
      ORDER BY DRCR      
    END      
          
  IF @flag = 2      
    BEGIN      
      IF @tvamt = 0      
        BEGIN      
          SET TRANSACTION ISOLATION  LEVEL  READ  UNCOMMITTED      
                
          SELECT   TDATE = CONVERT(VARCHAR,L.VDT,103),      
                   CLTCODE = ISNULL(CLTCODE,''),      
                   ACNAME = ISNULL(ACNAME,''),      
                   VNO = L.VNO,      
                   DRCR = L.DRCR,      
                   LNO = L.LNO,      
                   VTYP = L.VTYP,      
                   CHEQUENO = ISNULL(DDNO,''), /*refno= l.refno ,*/      
                   AMT = L.VAMT,      
                   RELDT = CONVERT(VARCHAR,LL1.RELDT,103),      
                   BOOKTYPE = L.BOOKTYPE      
          FROM     LEDGER L WITH (nolock),      
                   LEDGER1 LL1 WITH (nolock)      
          WHERE    L.VTYP IN ('2','3','5','16',      
                              '17','19','20')      
                   AND CLTCODE <> @code      
                   /*AND SUBSTRING(LL1.REFNO,1,12)=SUBSTRING(L.REFNO,1,12)  and */      
                   AND LL1.VNO = L.VNO      
                   AND LL1.VTYP = L.VTYP      
                   AND LL1.BOOKTYPE = L.BOOKTYPE      
                   AND LL1.LNO = L.LNO      
                   AND LL1.DRCR = L.DRCR      
                   AND L.VNO IN (SELECT VNO      
                                 FROM   LEDGER L1 WITH (nolock)      
                                 WHERE  CLTCODE = @code      
                                        AND L.VTYP = L1.VTYP      
                                        AND BOOKTYPE = L1.BOOKTYPE)      
                   AND LL1.RELDT <> '1900-01-01 00:00:00.000'      
                   AND VDT >= @frmdate      
                   AND VDT < = @gdate      
                   AND L.DRCR LIKE @gdrcr      
          ORDER BY CONVERT(VARCHAR,L.VDT,101),      
                   L.VTYP,      
                   L.VNO,      
                   L.DRCR      
        END      
              
      IF @tvamt <> 0      
        BEGIN      
          SET TRANSACTION ISOLATION  LEVEL  READ  UNCOMMITTED      
                
          SELECT   TDATE = CONVERT(VARCHAR,L.VDT,103),      
                   CLTCODE = ISNULL(CLTCODE,''),      
                   ACNAME = ISNULL(ACNAME,''),      
                   VNO = L.VNO,      
                   DRCR = L.DRCR,      
                   LNO = L.LNO,      
                   VTYP = L.VTYP,      
                   CHEQUENO = ISNULL(DDNO,''),/* refno= l.refno ,*/      
                   AMT = L.VAMT,      
                   RELDT = CONVERT(VARCHAR,LL1.RELDT,103)      
          FROM     LEDGER L WITH (nolock),      
                   LEDGER1 LL1 WITH (nolock)      
          WHERE    L.VTYP IN ('2','3','5','16',      
                              '17','19','20')      
                   AND CLTCODE <> @code      
                   AND LL1.VNO = L.VNO      
                   AND LL1.VTYP = L.VTYP      
                   AND LL1.LNO = L.LNO      
                   AND LL1.DRCR = L.DRCR      
                   AND /*AND SUBSTRING(LL1.REFNO,1,12)=SUBSTRING(L.REFNO,1,12)  and */      
                       L.VNO IN (SELECT VNO      
                                 FROM   LEDGER L1 WITH (nolock)      
                                 WHERE  CLTCODE = @code      
                                        AND L.VTYP = L1.VTYP)      
                   AND LL1.RELDT <> '1900-01-01 00:00:00.000'      
                   AND VDT >= @frmdate      
                   AND VDT < = @gdate      
                   AND L.DRCR LIKE @gdrcr      
                   AND VAMT = @tVamt      
          ORDER BY CONVERT(VARCHAR,L.VDT,101),      
                   L.VTYP,      
                   L.VNO,      
                   L.DRCR      
        END      
    END      
        
  IF @flag = 3      
    BEGIN      
      SET TRANSACTION ISOLATION  LEVEL  READ  UNCOMMITTED      
            
      SELECT   L.DRCR,      
               SUM(VAMT)      
      FROM     LEDGER L WITH(nolock),      
               LEDGER1 L1 WITH(nolock)      
      WHERE    L.VTYP = L1.VTYP      
               AND L.BOOKTYPE = L1.BOOKTYPE      
               AND L.VNO = L1.VNO      
               AND VDT <= @gdate + ' 23:59:59'      
               AND (L1.RELDT = '1900-01-01 00:00:00.000'      
                     OR L1.RELDT > @gdate + ' 23:59:59')      
               AND CLTCODE = @CODE      
      GROUP BY L.DRCR      
    END

GO

