                  @@vtype =   
                        (  
                              SELECT DISTINCT   
                                    vtype   
                              FROM templedger2   
                              WHERE rtrim(sessionid) = rtrim(@sessionid)   
                                    AND vno = @vno  
        )   
                  SELECT   
                  @@party2 =   
                        (  
                              SELECT DISTINCT   
                                    party_code   
                              FROM templedger2   
                              WHERE rtrim(sessionid) = rtrim(@sessionid)   
                                    AND upper(rtrim(branch)) = 'ALL'   
                                    AND vno = @vno   
                                    AND costflag = 'A'   
                                    AND lno = 1  
                        )   
