                  @@costbreakup =   
                        (  
                              SELECT   
                                    count(*)   
                              FROM templedger2   
                              WHERE rtrim(sessionid) = rtrim(@sessionid)   
                                    AND category = 'BRANCH'   
                                    AND vno = @vno   
                                    AND costflag = 'C'  
                        )   
        
            if @@costbreakup > 0 and (@@vtype = '3' or @@vtype = '4')     
