                  DELETE   
                  FROM templedger2   
                  WHERE rtrim(sessionid) = rtrim(@sessionid)   
                        AND category = 'BRANCH'   
                        AND upper(branch) = 'ALL'   
                        AND vno = @vno   
                        AND costflag = 'A'   
        
                  DELETE   
                  FROM templedger2   
                  WHERE rtrim(sessionid) = rtrim(@sessionid)   
                        AND category = 'BRANCH'   
                        AND upper(drcr) = 'C'   
                        AND vno = @vno   
                        AND costflag = 'C'   
        
                  INSERT   
                  INTO templedger2   
                  SELECT   
                        category,   
                        branch,   
                        paidamt,   
                        vtype,   
                        vno,   
                        1,   
                        (   
                              CASE   
                                    WHEN drcr = 'D'   
                                    THEN 'C'   
                                    ELSE 'D'   
                              END  
                        ),   
                        costcode,   
                        booktype,   
                        sessionid,   
                        @@party2,   
                        costflag,   
                        1   
                  FROM templedger2   
                  WHERE rtrim(sessionid) = rtrim(@sessionid)   
                        AND category = 'BRANCH'   
                        AND vno = @vno   
