            INTO ledger2   
            SELECT   
                  vtype,   
                  vno,   
                  lno,   
                  drcr,   
                  paidamt,   
                  costcode=   
                        (   
                              SELECT   
                                    costcode   
                              FROM costmast c,   
 branchaccounts b   
                              WHERE DefaultAc = 1   
                                    AND rtrim(branchname) = rtrim(costname)  
                        ),   
                  BookType,   
                  upper(BrControlAc)   
            FROM templedger2 t,   
                  branchaccounts b   
            WHERE rtrim(sessionid) = rtrim(@sessionid)   
                  AND rtrim(branch) = rtrim(branchname)   
                  AND category = 'BRANCH'   
                  AND vno =@vno   
                  AND costflag = 'A'   
                  AND rtrim(branch) <> rtrim(@@mainbr)   
        
            INSERT   
            INTO ledger2   
            SELECT   
                  vtype,   
                  vno,   
                  lno,   
                  (   
                        CASE   
                              WHEN drcr= 'd'   
                              OR drcr = 'D'   
                              THEN 'C'   
                              ELSE 'D'   
                        END  
                  ),   
                  paidamt,   
                  c.costcode,   
                  BookType,   
                  upper(MainControlAc)   
            FROM templedger2 t,   
                  costmast c,   
                  branchaccounts b   
            WHERE rtrim(sessionid) = rtrim(@sessionid)   
                  AND rtrim(branch) = rtrim(costname)   
                  AND rtrim(BranchName) = rtrim(branch)   
                  AND category = 'BRANCH'   
                  AND vno =@vno   
                  AND costflag = 'A'   
                  AND rtrim(branch) <> rtrim(@@mainbr)   
      end    
      else    
      if @@OneBranchAll = 0     
      begin    
      set @@rcursor = cursor for    
            SELECT   
                  branch   
            FROM templedger2   
            WHERE category = 'BRANCH'   
                  AND rtrim(sessionid) = rtrim(@sessionid)   
                  AND vno = @vno   
                  AND branch <> 'ALL'   
                  AND costflag = 'A'   
            ORDER BY branch   
            open @@rcursor    
            fetch next from @@rcursor into @@branch     
            while @@fetch_status = 0    
            begin    
            SELECT @@costcode1 =   
                        (   
                              SELECT   
                                    costcode   
                              FROM costmast   
                              WHERE rtrim(@@branch) = rtrim(costname)  
                        )   
                  fetch next from @@rcursor into @@branch     
                  end    
                  close @@rcursor    
                  deallocate @@rcursor    
              
                  INSERT   
                  INTO ledger2   
                  SELECT   
                        vtype,   
                        vno,   
                        lno,   
                        drcr,   
                        paidamt,   
                        c.costcode,   
                        BookType,   
                        upper(party_code)   
                  FROM templedger2 t,   
                        costmast c   
                  WHERE rtrim(sessionid) = rtrim(@sessionid)   
                        AND rtrim(branch) = rtrim(costname)   
                        AND category = 'BRANCH'   
                        AND vno =@vno   
                        AND costflag = 'A'   
              
                  INSERT   
                  INTO ledger2   
                  SELECT   
                        vtype,   
                        vno,   
                        lno,   
                        drcr,   
                        paidamt,   
                        @@costcode1,   
                        BookType,   
                        upper(party_code)   
                  FROM templedger2 t   
                  WHERE rtrim(sessionid) = rtrim(@sessionid)   
                        AND rtrim(branch) = 'ALL'   
                        AND category = 'BRANCH'   
                        AND vno =@vno   
                        AND costflag = 'A'   
            end    
      end    
    
      if @costcenterflag  = 1    
      begin    
            INSERT   
            INTO ledger2   
            SELECT   
                  vtype,   
                  @vno,   
                  lno,   
                  drcr,   
                  paidamt,   
                  costcode,   
                  BookType,  
                  upper(party_code )   
            FROM templedger2 t   
            WHERE rtrim(sessionid) = rtrim(@sessionid)   
                  AND vno = @vno   
                  AND costflag = 'C'   
      end

GO

