            values (upper(@cltcode),@acname,@@vdt1,@relamt,rtrim(@ddno),@ChequeInName,@narration,0,@vamt,0,@vtyp,@vno,@BookType)  
   end  
        end  
  
end  
/* ------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------ */  
  
/* Inserting records in Matchdetails and Updating Billmatch */  
  
