				END
			), 
			ACNAME, 
			(
				CASE 
				WHEN SELL_BUY = 1 
				THEN 'D' 
				ELSE 'C' 
				END
			), 
			AMOUNT, 
			@VDT, 
			@VNO, 
			'', 
			0, 
