			CONVERT(VARCHAR(11), GETDATE(), 109), 
			PARTY_CODE, 
			@BOOKTYPE, 
			@ENTEREDBY, 
			CONVERT(VARCHAR(11), GETDATE(), 109), 
			@CHECKEDBY, 
			0, 
			'NSXFO' + RTRIM(LTRIM(@BILLDATE)) + '  Bill Posted', 
			@SETT_NO, 
			@SETT_TYPE, 
			BILL_NO, 
			A.ACCAT, 
			A.BRANCHCODE  
