   AND RP.CHQ_MODE <> 'N'          
  DROP TABLE #RECPAY_TABLE_TMP          
  DROP TABLE #RECPAY_TABLE          
  ROLLBACK TRAN          
  DELETE FROM          
   V2_UPLOADED_FILES          
  WHERE          
   U_FILENAME = @FNAME          
   AND U_MODULE = 'RECEIPT/PAYMENT UPLOAD'          
  RETURN          
 END          
/*--------------------------FINAL VALIDATION BASED ON UPDFLAG ------------------------*/          
SELECT          
 @@ERROR_COUNT = COUNT(1)          
FROM          
 (          
  SELECT DISTINCT          
   CLIENT_CODE          
  FROM          
   #RECPAY_TABLE          
  WHERE          
   UPDFLAG = 'N'          
 ) A          
IF @@ERROR_COUNT > 0          
 BEGIN          
  SELECT          
   'FILE CANNOT BE UPLOADED AS THE FOLLOWING CLIENTS DO NOT EXIST', 'NA','NA'          
  UNION ALL          
  SELECT DISTINCT          
   CLIENT_CODE, 'NA','NA'          
  FROM          
   #RECPAY_TABLE          
  WHERE          
   UPDFLAG = 'N'          
  DROP TABLE #RECPAY_TABLE_TMP          
  DROP TABLE #RECPAY_TABLE          
  ROLLBACK TRAN          
  DELETE FROM          
   V2_UPLOADED_FILES          
  WHERE          
   U_FILENAME = @FNAME          
   AND U_MODULE = 'RECEIPT/PAYMENT UPLOAD'          
  RETURN          
 END          
  /*    --------------------------AUTO GENERATION OF VNO ------------------------ */          
  SET @@VNOCUR = CURSOR FOR          
   SELECT DISTINCT          
    BANK_CODE,          
    BOOKTYPE          
   FROM          
    #RECPAY_TABLE WITH(NOLOCK)          
  OPEN @@VNOCUR          
  FETCH NEXT          
   FROM          
    @@VNOCUR          
   INTO          
    @@BANK_CODE,          
 @@BOOKTYPE          
  WHILE @@FETCH_STATUS = 0          
   BEGIN          
  /*--------------------------VALIDATION WITH USERRIGHTS TABLE ------------------------*/          
    SELECT          
     @@BACKDAYS = ISNULL(MAX(NODAYS), 0)          
    FROM          
     USERWRITESTABLE          
    WHERE          
     USERCATEGORY = @USERCAT          
     AND VTYP = @@VTYP          
     AND BOOKTYPE = @@BOOKTYPE          
    SELECT          
     @@BACKDATE = CONVERT(DATETIME, CONVERT(VARCHAR(11), GETDATE(), 109)) - @@BACKDAYS          
    SELECT          
     @@ERROR_COUNT = ISNULL(COUNT(VDT), 0)          
    FROM          
     #RECPAY_TABLE          
    WHERE          
     VDT < @@BACKDATE          
        
    IF @@ERROR_COUNT > 0          
     BEGIN          
      SELECT          
       'SOME OF THE VOUCHERS ARE HAVING BACK DATED VOUCHER DATES FOR WHICH RIGHTS HAVE NOT BEEN SET', 'NA','NA'          
      DROP TABLE #RECPAY_TABLE_TMP          
      DROP TABLE #RECPAY_TABLE          
      ROLLBACK TRAN          
      DELETE FROM V2_UPLOADED_FILES          
      WHERE          
       U_FILENAME = @FNAME          
       AND U_MODULE = 'RECEIPT/PAYMENT UPLOAD'          
      RETURN          
     END        
          
    IF @@BRANCHFLAG = 1          
     BEGIN          
      SELECT          
       @@BANKBRANCH = BRANCHCODE,          
       @@BANKCOST = ISNULL(C.COSTCODE, -1)          
      FROM          
       ACMAST A          
        LEFT OUTER JOIN          
        COSTMAST C          
        ON          
         (          
          A.BRANCHCODE = C.COSTNAME          
         )          
      WHERE          
       A.CLTCODE = @@BANK_CODE          
      IF @@BANKBRANCH <> 'ALL'          
       BEGIN          
        UPDATE          
         RP          
          SET COSTCODE = @@BANKCOST          
        FROM          
         #RECPAY_TABLE RP,          
         ACMAST A          
        WHERE          
         A.CLTCODE = RP.CLIENT_CODE          
         AND A.BRANCHCODE = 'ALL'          
        UPDATE          
         #RECPAY_TABLE          
          SET BANKCOST = @@BANKCOST          
        WHERE          
         BANK_CODE = @@BANK_CODE          
       END          
      ELSE          
       BEGIN          
        UPDATE          
         #RECPAY_TABLE          
          SET COSTCODE =          
           (          
            SELECT TOP 1          
             COSTCODE          
            FROM          
             COSTMAST          
            WHERE          
             COSTNAME = 'HO'          
           )          
        WHERE          
         COSTCODE = ''          
         AND BANK_CODE = @@BANK_CODE          
                            SET @@COSTCODEUPDATES = CURSOR FOR          
                                       SELECT          
                                             SERIAL_NO, COUNT(DISTINCT COSTCODE)          
                                       FROM          
                                             #RECPAY_TABLE WITH(NOLOCK)          
                                       WHERE          
          BANK_CODE = @@BANK_CODE          
                                       GROUP BY          
                                             SERIAL_NO          
                                       HAVING COUNT(DISTINCT COSTCODE)  > 1          
                            OPEN @@COSTCODEUPDATES          
                            FETCH NEXT          
                             FROM          
                              @@COSTCODEUPDATES          
                             INTO          
                              @@SERIAL_NO,          
                              @@COSTCODECOUNT          
                            WHILE @@FETCH_STATUS = 0          
                             BEGIN          
          UPDATE          
           #RECPAY_TABLE          
            SET BANKCOST =          
             (          
              SELECT TOP 1          
               COSTCODE          
              FROM     
               COSTMAST          
              WHERE          
               COSTNAME = 'HO'          
             )          
          WHERE          
           (BANKCOST = ''   OR BANKCOST IS NULL)          
           AND BANK_CODE = @@BANK_CODE          
                                           AND SERIAL_NO = @@SERIAL_NO          
                              FETCH NEXT          
                               FROM          
                                @@COSTCODEUPDATES          
                               INTO          
                                @@SERIAL_NO,          
                                @@COSTCODECOUNT          
                             END          
                            CLOSE @@COSTCODEUPDATES          
                            DEALLOCATE @@COSTCODEUPDATES          
                  UPDATE          
                   #RECPAY_TABLE          
                    SET BANKCOST = COSTCODE          
                  WHERE          
                   BANK_CODE = @@BANK_CODE          
                                        AND (BANKCOST = ''   OR BANKCOST IS NULL)          
       END          
     END          
    CREATE TABLE          
     #BANK_VNO          
     (          
      SRNO INT,          
      NEWSRNO INT IDENTITY (1, 1)          
     )          
    INSERT INTO          
     #BANK_VNO          
    SELECT          
     DISTINCT SERIAL_NO          
    FROM          
     #RECPAY_TABLE          
    WHERE          
     BANK_CODE = @@BANK_CODE          
     AND BOOKTYPE = @@BOOKTYPE       
          
    SELECT          
     @@MAXCOUNT = COUNT(DISTINCT SNO)          
    FROM          
     #RECPAY_TABLE          
          
    SELECT TOP 1          
     @@VDT = VDT          
    FROM          
     #RECPAY_TABLE          
    SELECT          
     LASTVNO          
    INTO #VNO          
    FROM          
     LASTVNO          
    WHERE          
     1 = 2          
    SELECT @@MAXVNO = MAX(NEWSRNO) FROM #BANK_VNO          
    INSERT          
    INTO #VNO          
     EXEC ACC_GENVNO_NEW          
      @@VDT,          
      @@VTYP,          
      @@BOOKTYPE,          
      @@STD_DATE,          
      @@LST_DATE,          
      @@MAXVNO          
    SELECT          
     @@NEWVNO = LASTVNO          
    FROM          
     #VNO          
    UPDATE          
     RP          
      SET VNO = CONVERT(VARCHAR(12),CONVERT(NUMERIC,@@NEWVNO) + (NEWSRNO - 1))          
    FROM          
     #RECPAY_TABLE RP,          
     #BANK_VNO BV          
    WHERE          
     RP.SERIAL_NO = BV.SRNO          
   DROP TABLE #VNO          
   DROP TABLE #BANK_VNO          
  FETCH NEXT          
   FROM @@VNOCUR          
   INTO          
    @@BANK_CODE,          
    @@BOOKTYPE          
 END          
