     CROSAC='',                    
     A.ACNAME,                    
     A.BRANCHCODE,                    
     L.LNO                    
    FROM                    
     LEDGER L,                    
     VMAST V,                    
     LEDGER2 L2,                    
     ACMAST A/*,                    
     COSTMAST C */                   
    WHERE                    
     L.VNO = L2.VNO                    
